css:
@-moz-document domain("finobe.com") {
.navbar-light.bg-faded {
    position: relative;
    background-color: #0059D1;
    height: 38px;
    bottom: 0;
    margin: 0;
    padding: 0;
    background-image:none;
}
.navbar-brand {
    background: url(https://web.archive.org/web/20120331113037im_/http://www.roblox.com/images/btn-logo.png) top left;
    display: inline-block;
    width: 123px;
    height: 38px;
    float: left;
}
.nav-scroller {
    height: 30px!important;
    background: url(https://web.archive.org/web/20120329191841im_/https://www.roblox.com/images/cssspecific/rbx2/topNav_blue.png)!important;
    background-position: 0 0!important;
    text-align: center!important;
    line-height: 20px;
}
.nav-scroller-inner>a.nav-link {
    color: rgb(255, 255, 255);
    font-size: 100%!important;
    font-weight: bold!important;
    font-family: Arial
}
.footer>.container {
      background: #e6e6e6;
    text-align: center;
    font-size: 13px;
    color: #555;
    width: 100%;
}
.footer a {
    color: #095fb5;
}

}
