css:
@-moz-document domain("spigotmc.org")
{
body {
    background-color: #F0F0F0;
}
#logo img {
    filter: hue-rotate(153deg);
    -webkit-filter: hue-rotate(153deg);
    -moz-filter: hue-rotate(153deg);
    -o-filter: hue-rotate(153deg);
    -ms-filter: hue-rotate(153deg);
}
#logoBlock .spigot_colorOverlay {
    background-color: rgba(61, 193, 150, .8);
}
#navigation .navTabs, .breadcrumb, .uix_breadCrumb_toggleList li.toggleList_item {
    background: #20e1ac linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%);
}
.pageWidth {
    min-width: 940px;
    width: 976px;
    max-width: 976px;
}
.sectionMain {
    border: 1px solid #d7edfc;
}
.messageList .message {
    background-color: rgb(255, 255, 255);
}
.messageUserBlock {
    background: #d7edfc url('http://bukkit.org/styles/flexile/xenforo/gradients/tab-selected-light.png') repeat-x bottom;
    border: 1px solid #d7fcf4;
    border-radius: 5px;
}
.messageUserBlock div.avatarHolder {
    background-color: #f0f7fc;
}
.messageUserBlock .arrow span {
    border-left-color: #f0f7fc;
}
.messageUserBlock h3.userText {
    background-color: rgba(0, 0, 0, 0);
}
.messageUserBlock .extraUserInfo {
    background-color: #f0f7fc;
}
::selection {
    background-color: #32fee5;
}
.avatar img, .avatar .img, .avatarCropper {
    border: 1px solid #a5e4d3;
    border-radius: 4px;
}
.message .likesSummary {
    background-color: rgba(0, 0, 0, 0);
    background: #f0f7fc url('http://bukkit.org/styles/flexile/xenforo/gradients/category-23px-light.png') repeat-x top;
    border: 1px solid #d7fcf4;
}
.secondaryContent {
    background: #f0f7fc url('http://bukkit.org/styles/flexile/xenforo/gradients/category-23px-light.png') repeat-x top;
    border-bottom: 1px solid #d7fcf4;
}
.button.primary {
    background-color: #20e1a2;
}
#userBar .navTabs .navTab.Popup.PopupContainerControl.PopupOpen, #userBar .navTabs .navTab.selected.PopupOpen .navLink {
    background-color: #20e1a2;
}
.Menu {
    border-top: 2px solid #20e1a2;
}
.heading, .xenForm .formHeader {
    background-color: #20e1a2;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-color: #20e1a2;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .resourceListSidebar h3 {
    background: #20e1a2 linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%);
}
.footer .pageContent {
    border-top: 3px solid #20e1a2;
}
#QuickSearch .primaryControls .uix_icon {
    background-color: #84fff0;
}
.navTabs .navTab.selected .navLink, .navTabs .navTab.PopupClosed.selected .navLink {
    color: #33a580;
}
.navTabs .navTab.Popup.PopupContainerControl.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink {
    background-color: #84fff0;
}
.primaryContent a {
    color: #10b386;
}
.button {
    background-color: #E0F1FC;
}
.sidebar .visitorPanel .username {
    color: #10b39f;
}
.discussionListItems .unread .title a {
    color: #10b39f;
}
.button:hover, .button[href]:hover, .buttonProxy:hover .button {
    background-color: #FFFFD2;
    color: #535353;
}
.navTabs .navTab.selected .navLink:hover {
    color: #33a586;
}
.footerLinks a[href]:hover {
    color: #33a586;
}
a:link, a:visited {
    color: #33a586;
}
.footer .choosers a:hover {
    color: #33a586;
}
.footer a, .footer a:visited {
    color: #33a586;
}
.discussionListItems .unread .lastPostInfo .username {
    color: #33a586;
}
a.callToAction:hover span {
    background-color: #2ce29d;
}
.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: #33a586;
}
.jumbotron {
    background-color: #62f7d5;
}
.navbar-inverse {
    background-color: #20e1ac;
}
.col-md-2 img {
    filter: hue-rotate(153deg);
    -webkit-filter: hue-rotate(153deg);
    -moz-filter: hue-rotate(153deg);
    -o-filter: hue-rotate(153deg);
    -ms-filter: hue-rotate(153deg);
}
.box {
    background-color: white;
}
.box-icon {
    background-color: #20e1ac;
}
.btn-warning {
    background-image: radial-gradient(circle, #62f7d5 80%, #20e1ac 81%);
}
.btn-warning:hover, .btn-warning:focus, .btn-warning.focus, .btn-warning:active, .btn-warning.active, .open>.dropdown-toggle.btn-warning {
    background-color: #20e1ac;
}
.btn-warning {
    background-color: #20e1ac;
}
.gadget.color3 .dashboard-item-header, .gadget-colors .color3>a {
    background-color: #20e1ac;
}
.gadget.color1 .dashboard-item-header, .gadget-colors .color1>a {
    background-color: #62f7d5;
}
.aui-header {
    background: #20e1ac;
    border-bottom-color: #20e1ac;
}
.aui-dropdown2.aui-style-default .active {
    background-color: #62f7d5;
}
.aui-header .aui-button.aui-button-primary.aui-style {
    background-color: #98f9d9;
    background-image: linear-gradient(to bottom, #98d6f9, #62f7d5);
}
}
