css:
/* i really want this to be global */
@keyframes scale-in-center {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

@keyframes fade-in {
    0% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}


:root {
    --main-color: 75, 75, 163;
}

body {
    background: white;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Noto Sans", Ubuntu, Cantarell, "Helvetica Neue", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
}

.button,
.button:hover {}

.phui-two-column-content.phui-two-column-footer {
    display: flex;
    flex-direction: column;
}

[data-sigil=phui-comment-form] {
    order: -1;
}

.phabricator-side-menu {
    background: white !important;
}

.phabricator-main-menu-background {
    background: rgb(75, 75, 163) !important;
}

.phabricator-main-menu-eye {
    background-size: 25px 25px !important;
    background-repeat: no-repeat;
    background-position: 50% 50% !important;
}

.dashboard-box {
    animation: fade-in 0.5s cubic-bezier(0.250, 0.460, 0.450, 0.940) both;
    animation-delay: 0.5s;

    box-shadow: 0 0.3px 0.4px rgba(75, 75, 163, 0.02),
    0 0.7px 0.9px rgba(75, 75, 163, 0.028),
    0 1.3px 1.8px rgba(75, 75, 163, 0.035),
    0 2.2px 3.1px rgba(75, 75, 163, 0.042),
    0 4.2px 5.8px rgba(75, 75, 163, 0.05),
    0 10px 14px rgba(75, 75, 163, 0.07);
    border-color: rgba(75, 75, 163, 0.07);
    transition: 0.3s;
}

.dashboard-box:hover {
    transform: scale(1.002) translate(0, -1px) !important;
    box-shadow: 0 0.9px 1.2px rgba(75, 75, 163, 0.02),
    0 2.2px 2.9px rgba(75, 75, 163, 0.028),
    0 4.1px 5.5px rgba(75, 75, 163, 0.035),
    0 7.4px 9.8px rgba(75, 75, 163, 0.042),
    0 13.8px 18.4px rgba(75, 75, 163, 0.05),
    0 33px 44px rgba(75, 75, 163, 0.07);
}
