css:
@-moz-document domain("wykop.pl") {
*::-webkit-scrollbar
{
	width: 10px;
	height: 10px;
	background: #1e1e1e !important;
	border: 1px solid #252525 !important;
}

*::-webkit-scrollbar-button
{
	display: none;
}

*::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track
{
	border: 0 !important;
	box-shadow: none;
}

*::-webkit-scrollbar-thumb
{
	min-height: 28px;
	background: #3a3a3a !important;
}

*::-webkit-scrollbar-corner, *::-webkit-scrollbar-track
{
	background: #292929 !important;
}

::selection
{
	background: #B6B6B6;
	color: black;
}

body
{
	background-color: #0D0D0D !important;
	color: #ccc !important;
}

.type-light-caution, .type-light-warning
{
	background-color: #2f2d23 !important;
}

.summary
{
	background: #333;
}

ul.related li > div div.content
{
	border: 1px solid #292929;
}

ul.related li > div div.content span
{
	background: #1C1C1C!important;
}

.ac_results
{
	border: 1px solid #45484a;
	background-color: #000;
}

.r-block, .grid-right > .r-block, .grid-right > div
{
	background: #1C1C1C!important;
}

.data a
{
	color: white!important;
}

.space information bdivider a
{
	color: black!important;
}

.r-block, .grid-right > .r-block, .grid-right > div
{
	background: #001C1C1C !important;
}

.drop-cal
{
	background: #1C1C1C !important;
}

.drop-cal > div ul
{
	background: #1C1C1C !important;
	border: 1px solid #1C1C1C;
}

a
{
	color: #aa5 !important;
}

a:hover
{
	color: #d9d9d9 !important;
}

em.mark-number
{
	background: #702321 !important;
}

textarea
{
	background-color: #121212 !important;
	border: 1px solid #2E2E2E;
	color: #b6b6b6;
}

input
{
	color: #c5c5c5!important;
	background-color: #1B1B1B !important;
	border-color: #1B1B1B !important;
}

#site
{
	background: #0D0D0D !important;
}

#site .article h2 a, #itemsStream .article h2 a
{
	color: #a70 !important;
}

#site .article h2 a:hover, #itemsStream .article h2 a:hover
{
	color: #a70 !important;
	text-decoration: underline !important;
}

#itemsStream .article .description a
{
	color: #e7e7e7 !important;
}

#itemsStream .article .description a:hover
{
	color: black !important;
	border: 0 !important;
}

#itemsStream .author
{
	border-color: #1a1a1a !important;
}

.type-light-info
{
	color: white;
	background: #1C1C1C !important;
}

.bdivider, .tdivider
{
	border-color: #0D0D0D !important;
}

#nav
{
	background: #131313 !important;
}

#nav .nav li.active a, #site .nav li.active a
{
	background: #702321 !important;
	color: #d9d9d9 !important;
	border-color: #702321 !important;
}

#nav .nav li a:hover, #site .nav li a:hover
{
	border-color: #702321 !important;
	color: #d9d9d9 !important;
}

.nav
{
	border-color: #1a1a1a !important;
}

.mark-bg
{
	background: #1a1d1e !important;
}

.tag.create
{
	color: #565859 !important;
}

.color-1.affect
{
	color: #5b433c !important;
}

.color-2.affect
{
	color: #5b433c !important;
}

.r-block h4
{
	color: #702321 !important;
	border-color: #1a1a1a !important;
}

#footer h4
{
	color: #702321 !important;
	border-color: #1a1a1a !important;
}

.diggbox span:first-child
{
	display: block;
	width: 49px;
	height: 42px;
	line-height: 42px;
	font-weight: bold;
	color: #702321;
	background: url('data:image/png;base64,') !important;
}

.rbl-block
{
	background-color: #1B1B1B !important;
	border: 0 !important;
}

.menu-list li
{
	border: 0 !important;
}

.description
{
	border-top: 1px solid #2A2A2A !important;
	background: #1C1C1C !important;
}

.light
{
	color: #95a5a6;
}

.showSpoiler, .show-more
{
	border-color: #2A2A2A !important;
	background: #1C1C1C !important;
	box-shadow: 0 1px 0 0 rgba(128, 128, 128, .2);
}

#itemsStream
{
	background: #131313 !important;
}

.comments-stream
{
	background: none !important;
}

.comments-stream li
{
	background: #131313 !important;
	border: 0 !important;
}

.r-block
{
	background: #131313 !important;
	border: 0 !important;
}

.button
{
	border-color: #2A2A2A !important;
	background: #1C1C1C !important;
	color: #C3C3C3 !important;
}

.button:hover
{
	background: #1a1a1a !important;
	color: #C3C3C3 !important;
}

.article > div > div .text a:hover
{
	background: #C3C3C3;
}

.button.selected
{
	background: #702321 !important;
	border: 0 !important;
}

#footer
{
	background-color: #131313 !important;
	border-top: 1px solid #161616 !important;
}

body a:hover
{
	color: #a70 !important;
}

#new-frame
{
	background: #324a43;
}
}
