css:
@-moz-document url-prefix("https://scoresaber.com") {
:root {
--color-highlight: rgba(140, 0, 0, .8);
--color-ahead: rgb(143, 72, 219);
--color-behind: rgb(254, 0, 6);
}

.navbar {
background: black;
}

.navbar-link {
color: black;
background-color: #fffaae !important;
}

.navbar-dropdown {
background-color: black;
border-top: 2px solid #fffaae !important;
}

.navbar-dropdown a.navbar-item:hover {
background-color: #ff0c0b;
}

a.navbar-item:hover {
color: #fffaae !important;
background-color: black;
}

#settings_dialogue {
background-color: black !important;
}

.label {
color: #fffaae;
}

.checkbox {
color: #fffaae;
}

.checkbox:hover {
color: #cb0b0b;
}

.is-checkradio[type=checkbox]+label::after, .is-checkradio[type=checkbox]+label:after {
border-color: #cb0b0b;
}

.navbar.has-shadow {
box-shadow: 0 3px 3px 0px red;
}

tr {
background: rgba(0, 0, 0, .8);
border: 1px solid rgba(0, 0, 0);
}

.section {
background: url(https://i.ytimg.com/vi/E4Ls61G5VBo/maxresdefault.jpg) no-repeat fixed;
background-size: cover; 
}

.box {
background: rgba(0, 0, 0, .2);
background-size:cover; 
}

.box.has-shadow {
background-color: rgba(0, 0, 0, .2) !important;
}

a {
color: #fffaae !important;
font-family: Brush Script MT;
font-size: 20px;
}

a:hover {
color: #ffffff;
font-family: Brush Script MT;
}

strong {
color: #ffffff;
}

ul {
background-color: rgba(0, 0, 0, .8);
border: 1px solid rgba(0, 0, 0);
border-radius: 7px;
padding-left: 22px;
width: 320px;
}

.content ul {
margin-left: 0px;
}

li {
color: #fffaae;
font-family: Snell;
}

.content table thead td, .content table thead th {
color: #fffaae;
}

.content table th {
color: #fffaae;
}

span.songBottom.time {
color: #ffffff;
}

span.scoreTop.ppValue {
color: #fffaae !important;
}

span.scoreTop.ppLabel {
color: #fffaae !important;
}

span.scoreTop.ppWeightedValue {
color: #ffffff;
}

span.scoreBottom {
color: #ffffff;
}

.pagination-previous, .pagination-next, .pagination-link {
border-color: #ffffff;
color: #fffaae;
}

.pagination-link.is-current {
background-color: #fffaae;
border-color: #ffffff;
color: black !important;
}

.pagination-previous:focus, .pagination-next:focus, .pagination-link:focus {
border-color: red;
}

.footer {
background: black;
}

span.songTop.pp {
color: #fffaae !important;
font-size: 22px;
}

span.songTop.pp:hover {
color: red !important;
}
span.songTop.mapper {
font-size: 22px;
}

span.songTop.mapper:hover {
color: red !important;
}

td.song a {
color: #fffaae !important;
font-size: 14px;
font-family: Snall;
}

td.author a {
color: #fffaae !important;
font-size: 14px;
font-family: Snall;
}

th {
font-size: 14px;
font-family: Snall;
}

td {
font-size: 14px;
font-family: Snall;
}

h5 {
color: #fffaae !important;
}

h5.title.is-5 {
background: rgba(0, 0, 0, .8);
border-radius: 5px;
width: fit-content;
height: 25px;
margin-right: 10px;
margin-top: -11px;
}

h4.title.is-4 {
color: black !important;
}

h5.title.is-5>img {
margin-left: 5px;
}

.button.is-info {
background: black;
color: #fffaae;
}

.button.is-info:hover, .button.is-info.is-hovered {
background: #fffaae;
color: black;
}

.select select {
background-color: rgba(0, 0, 0, .8);
color: #fffaae;
}

.select select:focus, .select select.is-focused, .select select:active, .select select.is-active {
border-color: red;
}

.select:not(.is-multiple):not(.is-loading)::after {
border-color: red;
}

.input:focus {
border-color: darkred;
}

.input, .textarea {
background-color: black;
color: #fffaae !important;
border-color: darkred;
}

.rankChart {
background-color: rgba(0, 0, 0, .8);
border: 1px solid rgba(0, 0, 0);
border-radius: 7px;
}

.column.is-narrow.avatar {
background-color: rgba(0, 0, 0, .8);
border: 1px solid rgba(0, 0, 0);
border-radius: 7px;
height: 382px;
}

.pagination .button {
background-color: rgba(0, 0, 0, .8) !important;
margin: 4px;
}

a.button {
color: #fffaae !important;
}

.scores {
color: #fffaae !important;
}

.mods {
color: #fffaae !important;
}

.timeset {
color: #fffaae !important;
}

.score {
color: #fffaae !important;
}

.percentage {
color: #fffaae !important;
}

.stars {
color: #fffaae !important;
}

.rank {
color: #fffaae !important;
}
}
