css:
@-moz-document url-prefix("https://www.curseforge.com/") {
a,
p,
span,
.curseforge-header .top-nav__nav-link,
.footer-link,
.button__text,
.user-menu__item,
.text-primary-500.font-bold.text-lg,
.font-bold.text-sm.leading-loose,
.text-primary-500,
.source-quote a,
.source-quote .quote-source .quote-author,
.p-comment-wrapper a {
	color: #E6DB74;
}

.game-header {
	background: none;
}

.game-header .game-header__art {
	opacity: 0.2;
}

.listing-filters div div {
	margin-left: 1px;
	margin-right: 1px;
}

.mr-2.pb-1.border-b.border-primary-500 {
	padding: 5px;
}

.mr-3,
.pagination.pagination-top.flex.items-center {
	margin: 0;
	margin-right: 8px;
}

.text-primary-500.font-bold.text-lg:hover,
.text-base.leading-normal.font-bold:hover,
.text-gray-400 {
	color: #E69F66;
}

.bg-primary-0,
.body-privatemessage-index .p-pm-filter .user-actions .user-action.selected,
.body-privatemessage-index .p-pm-filter .user-actions .user-action.selected a,
.rounded:hover {
	border-radius: 0;
	background-color: #E69F66;
}

.bg-white,
.curseforge-header,
.bg-charcoal,
.bg-primary--100,
.tag,
.listing-container:not(.custom-formatting) .alert.no-results {
	background-color: #75715E;
}

input[type="text"],
input[type="search"],
#users li.user-list-item,
#users li.user-list-item div a img,
#projects li.latest-post-item.project-list-bubble-item,
#filter-related-dependents,
#filter-project-status,
#field-language,
#field-markup-mode,
#select2-drop,
#s2id_field-timezone a,
#s2id_field-time-zone a,
#field-range-start-time-time,
#field-range-end-time-time,
#users li,
#filter-game-version,
#filter-sort,
#field-search,
#field-action,
#field-pm-folder,
#field-submit,
.public-project-comment-sub.subscriptions-tab.project-list-bubble-item,
.listing-container.listing-container-table:not(.custom-formatting) table thead tr th,
.listing-container.listing-container-table:not(.custom-formatting) table tbody tr,
.box,
.followers,
.posts,
.likes,
.button,
.border-primary-500,
.e-myApiTokens-create-name.input.mr-4 input[type="text"],
.my-auto.mr-4.text-gray-400,
.listing.listing-.flex.flex-wrap.w-full li,
.comment-right-side-user-action .label,
.user-actions.b-userAction.b-userAction-a.covert-list a span {
	color: #F8F8F2;

	border-radius: 0;
	background-color: #3E3D32;
	border: none;
}

table thread,
a.bg-white,
#field-,
.form-submit.flex.form-gray-footer button,
.button,
.border-color-gray--100,
.button.button--icon-only.button--sidebar,
.border,
.tag,
.user-avatar div a img,
.comment-right-side-user-action .label,
.user-actions.b-userAction.b-userAction-a.covert-list a span {
	border: inherit;
	border-radius: 0;
	border-width: 1px;
	border-color: #E6DB74;
	
	padding: 4px;
}

.comment-right-side-user-action .label,
.user-actions.b-userAction.b-userAction-a.covert-list a span {
	background-color: #75715E;
}


.project-avatar-64 img,
.rounded {
	border-radius: 0;
}

.switch-checkbox {
	grid-column: 2;
	grid-row: 1;

	margin: 0;
	margin-left: 5px;
}

.container-div-always-show-ads .field.flex,
.container-div-invisible .field.flex,
.field-description,
.p-notification-settings .field.flex {
	display: grid;

	grid-template-columns: 370px auto;
	grid-template-rows: none;
}

.container-div-always-show-ads div div label,
.container-div-invisible div div label,
.field-description label,
.p-notification-settings div label {
	display: flex;
	align-items: center;
}

.container-div-always-show-ads div div label::after,
.container-div-invisible div div label::after,
.field-description label::after,
.p-notification-settings div label::after {
	content: '';
	flex: 1;
	margin-left: 1rem;
	height: 1px;
	background-color: #F8F8F2;
}

.container-div-always-show-ads .field.flex .mb-1,
.container-div-invisible .field.flex .mb-1,
.field-description,
.p-notification-settings .field.flex {
	box-sizing: content-box;
	width: 370px;
}

div,
#field-day-selection label span,
#filter-related-dependents,
.text-charcoal,
.text-gray-500,
.icon,
.icon.icon-gray-500,
.text-xl,
.text-lg,
.text-sm,
.standard-date,
.s_myAccount .p-delivery-options .time-selection span.text-to,
.pagination .pagination-next:not(.pagination-prev--inactive):not(.pagination-next--inactive) path,
.pagination .pagination-prev:not(.pagination-next--inactive):not(.pagination-prev--inactive) path,
.p-comment-wrapper p,
.project-detail__content p {
	color: #F8F8F2;
	fill: #F8F8F2;
}

.pagination {
	margin: 5px;

	flex-direction: row;
	justify-content: center;
}

#nav-project-files,
#nav-project-comments,
.pagination .pagination-item span,
.pagination span,
.p-comment-index .j-comment-link,
nav ul .p-nav-item a .b-list-label {
	font-size: 24px;
}

li.user-action.b-userAction-item a,
.button.button--hollow,
.dropdown,
.source-quote {
	border-radius: 0;
	background-color: #75715E;
	border-color: #E6DB74;
}

.bg-red-500,
.bg-twitch-offset {
	background-color: #F92672;
}

.bg-prime-blue {
	background-color: #66D9EF;
}

.bg-green-500,
.switch-checkbox input:checked + .checkbox-slider {
	background-color: #A6E22E;
}

.body-publicproject-details .listing.listing-comments li.p-comments .comment-indentation {
	border-color: #75715E;
}

.root-content,
.user-menu__tail,
.bg-accent,
.form-submit.flex.form-gray-footer,
.form-actions.form-footer.flex.form-gray-footer,
ul .u-dropDownMenu,
.featherlight .featherlight-content {
	background-color: #272822;

	border-radius: 0;
	border-color: #F8F8F2;
}

.featherlight .featherlight-content {
	border-bottom: 25px solid #272822;
}

.p-comment-edited,
.p-comment-postdate {
	margin: 6px;
	padding: 4px;
	
	background-color: #75715E;
}

.p-comment-postdate {
	margin-left: auto;
}

.dd-arrow-lt::before {
    border-right-color: #272822;
}
.dd-arrow-lt::after {
    border-right-color: #F8F8F2;
}

.dd-arrow-tr:before {
    border-bottom-color: #272822;
}
.dd-arrow-tr::after {
    border-bottom-color: #F8F8F2;
}
}
