css:
@-moz-document domain("youtube.com") {
.video-extras-sparkbar-likes {background: #00cc00} /* Green like ratio */
.like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content {color: #00cc00} /* Green like numbers */

.video-extras-sparkbar-dislikes {background: #cc0000} /* Red like ratio */
.like-button-renderer-dislike-button.yt-uix-button-toggled .yt-uix-button-content {color: #cc0000} /* Red dislike numbers */



 
}
