css:
@-moz-document domain("github.com") {
div.tooltipster-content{
	background-color: #2e2e2e!important;	
	border-color: black!important;
}
.ghh-theme-github .tooltipster-bottom .tooltipster-arrow-background{
	border-bottom-color: #2e2e2e!important;	
}
.ghh-theme-github .tooltipster-bottom .tooltipster-arrow-border{
	border-bottom-color: rgba(0,0,0,0.8)!important;
}
.ghh-theme-github .tooltipster-top .tooltipster-arrow-background{
	border-top-color: #2e2e2e!important;	
}
.ghh-theme-github .tooltipster-top .tooltipster-arrow-border{
	border-top-color: rgba(0,0,0,0.8)!important;
}
.ghh-theme-github .tooltipster-right .tooltipster-arrow-background{
	border-right-color: #2e2e2e!important;	
}
.ghh-theme-github .tooltipster-right .tooltipster-arrow-border{
	border-right-color: rgba(0,0,0,0.8)!important;
}
.ghh-theme-github .tooltipster-left .tooltipster-arrow-background{
	border-left-color: #2e2e2e!important;	
}
.ghh-theme-github .tooltipster-left .tooltipster-arrow-border{
	border-left-color: rgba(0,0,0,0.8)!important;
}
div.tooltipster-content hr{
	border-color: #87C9C3!important;
}
div.tooltipster-content a,
div.tooltipster-content h1,
div.tooltipster-content h2,
div.tooltipster-content h3,
div.tooltipster-content h4,
div.tooltipster-content h5,
div.tooltipster-content h6,
div.tooltipster-content svg.octicon{
	color: #87C9C3!important;	
}
div.tooltipster-content p,
div.tooltipster-content span,
div.tooltipster-content li{
	color: white!important;	
}
div.tooltipster-content button{
	background-color: #00a697!important;	
}
.ghh-readme pre, .ghh-issue-body pre{
	filter: invert(1);
	background-color: #cecece;
}
}
