css:
@-moz-document domain("niezalezna.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #018086 #1c1c1c;
}
a{
    color: #018086;
}
/*HEADER*/
#header,#bn.breakingNews{
    background: #1c1c1c;
    box-shadow: none;
}
#header .logo img{
    filter: invert(100%);
}
#header .headerLeft, #header .headerLeft a{
    color: #018086;
}
#bn.bn-small > ul > li > a{
    background: #1c1c1c;
    color: #018086;
}
#header ul li a:hover,#bn.bn-small > ul > li > a:hover{
    background: #018086;
    color: #fff;
}
#header .headerLeft svg path{
    fill: #fff;
}
/*MENU-LEFT*/
#menuLeft{
    background: #1c1c1c;
}
.menu ul li a{
    color: #018086;
}
.menu ul li a:hover{
    background: #018086;
    color: #fff;
}
.btnContact a:hover{
    background: #1c1c1c;
    color: #018086;
}
/*MENU-LEFT*/
/*MENU*/
#hmenu ul li a{
    color: #018086;
}
#hmenu ul li a:hover{
    background: #1c1c1c;
}
/*MENU*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
a.uitemUnderline:hover .uitemTitle p{
    text-decoration: none;
    color: #018086;
}
.panel,.panelHorizontal,#panelRegionSelect .divTableCell,.uitemBox,.pressTopItemHorizontal{
    background: #1c1c1c;
    box-shadow: none;
}
#latestNewsList a,.opinionAuthor{
    color: #018086 !important;
}
#latestNewsList a:hover,.uitemList:hover.uitemAH85IL .uitemTitle p,.pagenav{
    color: #fff !important;
}
#panelRegionSelect ul li{
    background: #1c1c1c;
    color: #018086;
}
#panelRegionSelect ul li:hover{
    background: #018086;
    color: #fff;
}
.panelVideo{
    background: #018086;
}
.panelTitle,.panelTitle a,.uitemList.uitemAH85IL .uitemTitle p,.pressTopItemHorizontal .articleHorizontalTitle{
    color: #018086;
}
.showallarr svg{
    filter: invert(100%);
}
#puszczatv_right{
    background: #1c1c1c !important;
}
#puszczatvRightTxt{
    color: #00896B !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
#loader{
    background: #1c1c1c;
}
#loader a{
    color: #018086;
}
#loader .divTableCell:hover{
    background: #018086;
}
#loader .divTableCell:hover a{
    color: #fff;
}
.articleInfoHeader,.articleContent h1,.authorName, .authorName a,.articleBody strong{
    color: #018086;
}
.authorContact a,.articleLead,.articleBody{
    color: #fff;
}
.emoticons ul .l1{
    background: #1c1c1c;
    border: 2px solid #018086;
    border-right: 1px solid #018086;
}
.emoticons ul .l1 .ocena-inner,.emoticons ul .l1 .e1{
    background: #018086;
}
.emoticons ul .l1 .nmy,.emoticons ul .l1 .my{
    color: #018086;
}
.emoticons ul .l2{
    background: #1c1c1c;
    border: 2px solid #E74C3C;
    border-left: 1px solid #E74C3C;
}
.emoticons ul .l2 .ocena-inner,.emoticons ul .l2 .e2{
    background: #E74C3C;
}
.emoticons ul .l2 .nmy,.emoticons ul .l2 .my{
    color: #E74C3C;
}
.emoticons .l3 .ocena-btn,.emoticons .l4 .ocena-btn,.emoticons .l5 .ocena-btn{
    background: #1c1c1c;
    border: 1px solid #018086;
}
.emoticons .l3 .nmy,.emoticons .l4 .nmy,.emoticons .l5 .nmy,.articleBody a.uitemNoUnderline p{
    color: #fff;
}
.articleSource{
    color: #fff !important;
}
.articleSource img{
    filter: invert(100%);
}
.articleBoxName{
    color: #018086;
}
.articleTagList span{
    background: #018086;
    border: 1px solid #018086;
}
.articleTagList span:hover{
    background: #1c1c1c;
    border: 1px solid #018086;
}
.articleBody 
/*KOMENTARZE*/
.panelTitle{
    border-bottom: 1px solid #018086;
}
.comment-text textarea,.input-text{
    background: #1c1c1c;
    border: 1px solid #018086;
    color: #fff;
}
.comment-name,#comments .comment-content{
    color: #fff;
}
.kciuk{
    color: #fff;
}
.uitemComments p{
    color: #fff;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
/*KELANDARIUM*/
.bgWhite,.calendarMonthMenu ul li a,.calendarDayMenu ul li a,.calendarDayMenu ul li:hover, .calendarDayMenu ul li:hover a .dayMenuDay, .calendarDayMenu ul li:hover a .dayMenuTxt{
    background: #1c1c1c;
}
.pageHeader h1 a,.calendarMonthMenu ul li a .monthMenuMonth,.calendarDayMenu ul li a .dayMenuDay,.uitemCalendarEvent .uitemLeft .uitemEventDay,.calendarDayMenu ul li:hover a .dayMenuTxt{
    color: #018086;
}
.calendarMonthMenu ul li a .monthMenuYear,.calendarDayMenu ul li a .dayMenuTxt,.messageTxt,.uitemCalendarEvent .uitemLeft,.uitemList .uitemTitle{
    color: #fff;
}
.calendarDayMenu ul li a{
    border-bottom: 7px solid #018086;
}
/*KELANDARIUM*/
/*POGODA*/
.pageHeader h1{
    color: #018086;
}
#pogoda h3,#miasta ul li a{
    color: #fff;
}
#miasta ul li a:hover{
    color: #018086;
    text-decoration: none;
}
/*POGODA*/
/*WYSZUKIWANIE*/
.searchHeader{
    background: #1c1c1c;
}
.searchHeader h1,.articleHorizontalInfo .articleHorizontalTitleMiddle{
    color: #018086;
}
.contentSearch .searchResultBox p,.searchInfo,.articleItemHorizontal:hover .articleHorizontalTitleMiddle,.articleHorizontalInfo .articleHorizontalLead{
    color: #fff;
}
.searchBox .searchInput{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #018086;
}
.articleItemHorizontal{
    background: #1c1c1c;
    border: 1px solid #018086;
}
/*WYSZUKIWANIE*/
/*ZGŁOŚ-TEMAT*/
.form.form-horizontal .control-label{
    color: #fff;
}
.form.form-horizontal .form-control{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #018086;
}
.btnLogin:hover{
    background: #018086;
}
/*ZGŁOŚ-TEMAT*/
/*PROFIL-AUTORA*/
.opinionContact{
    color: #fff;
}
/*PROFIL-AUTORA*/
/*PODSTRONY*/
/*FOOTER*/
#footer2 b{
    color: #fff;
}
#footer2:after{
    content: "| Ciemny motyw by Maciejozaman";
    color: #018086;
}
/*FOOTER*/
}
