css:
@-moz-document domain("avenoel.org") {
/* 

STYLE CSS SPATIAL CRÉÉ PAR ATOMISTE LE 12 JANVIER 2020
POUR QUE LE STYLE FONCTIONNE IL VOUS FAUT 2 POLICES CUSTOMISÉES
VOUS POUVEZ LES TELECHAREGR ICI :
> http://atomiste.000webhostapp.com/css/fonts/Prototype.ttf
> http://atomiste.000webhostapp.com/css/fonts/quadrangle.ttf
UNE FOIS TELECHARGÉS, CLIQUEZ SUR EL FICHIER TTF ET CLIQUEZ SUR "INSTALLER"

*/
.message-content,
.message-content-quote {
    font-size: 17px!important;
    color: #e3e3e3;
    font-family: "prototype";
    src: url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff") format("woff");
    text-shadow: #1e1e1e 3px 0 8px;
    letter-spacing: 1.5px;
}

.topic-title,
.bloc-title {
    font-size: 30px!important;
    color: #e3e3e3!important;
    text-shadow: #000 1px 0 5px;
    font-weight: bolder;
    font-family: "quadrangle"!important;
    src: url("http://atomiste.000webhostapp.com/css/fonts/quadrangle-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/quadrangle-webfont.woff") format("woff");
}

a,
li {
    color: #e3e3e3;
    font-family: "prototype";
    src: url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff") format("woff");
    font-size: 14px;
    letter-spacing: 1px;
}

.topics-author a {
    color: #e3e3e3;
    font-family: "prototype";
    src: url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff") format("woff");
    font-weight: lighter;
    font-size: 13px;
}

.tex2jax_ignore {
    background-image: url('https://image.winudf.com/v2/image1/Y29tLnF1aWVsYW54MC5TcGFjZVdhbGxwYXBlcnM0S19zY3JlZW5fMF8xNTUxNjQzNTA5XzAwNg/screen-0.jpg?fakeurl=1&type=.jpg')!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
}

.navbar-logo {
    content: url('https://nsa40.casimages.com/img/2020/01/12/200112072008361600.png')!important;
    min-width: 30px;
    min-height: 70px;
    padding: 5px;
}

tr.active,
tr.active td {
    background-color: #0042ffb0!important;
}

.navbar-logo:after {
    background-image: url('https://nsa40.casimages.com/img/2020/01/12/200112072008361600.png')!important;
    visibility: visible;
}

.__web-inspector-hide-shortcut__ {
    visibility: hidden;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: rgba(11, 21, 45, .8);
}

.table-striped > tbody > tr:nth-of-type(even) {
    background-color: rgba(49, 41, 53, .8);
}

.breadcrumb {
    visibility: hidden;
}

.glyphicon-chevron-right,
.glyphicon-chevron-left {
    color: #fff;
    outline-style: ridge;
    outline-offset: 2px;
    outline-width: 10px;
    outline-color: rgba(11, 21, 45, .8);
}

.odd .message-content-quote {
    background-color: rgba(11, 21, 45, .8);
}
.message-content-quote {
    background-color: rgba(49, 41, 53, .8);
}


.col-xs-1,
.col-sm-1,
.col-md-1,
.col-lg-1,
.col-xs-2,
.col-sm-2,
.col-md-2,
.col-lg-2,
.col-xs-3,
.col-sm-3,
.col-md-3,
.col-lg-3,
.col-xs-4,
.col-sm-4,
.col-md-4,
.col-lg-4,
.col-xs-5,
.col-sm-5,
.col-md-5,
.col-lg-5,
.col-xs-6,
.col-sm-6,
.col-md-6,
.col-lg-6,
.col-xs-7,
.col-sm-7,
.col-md-7,
.col-lg-7,
.col-xs-8,
.col-sm-8,
.col-md-8,
.col-lg-8,
.col-xs-9,
.col-sm-9,
.col-md-9,
.col-lg-9,
.col-xs-10,
.col-sm-10,
.col-lg-10,
.col-xs-11,
.col-sm-11,
.col-md-11,
.col-lg-11,
.col-xs-12,
.col-sm-12,
.col-md-12,
.col-lg-12 {
    position: relative;
    min-height: -50px!important;
    min-width: 1250px!important;
    padding-left: 0px;
    padding-right: 5px;
    margin-top: -20px;
    margin-left: -50px;
    background-color: #f000;
    font-family: "prototype";
    font-size: 15px!important;
    src: url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff") format("woff");
}

.topic-message.odd {
    background: rgba(70, 64, 87, .8);
    outline-style: ridge;
    outline-offset: -8px;
    outline-width: 8px;
    outline-color: #1a2b687d;
}

.topic-message {
    background: rgba(41, 56, 96, .8);
    outline-style: ridge;
    outline-offset: -8px;
    outline-width: 8px;
    outline-color: #40146f7a;
}

button,
.btn-primary,
.btn-danger,
.btn {
    outline-style: ridge;
    outline-offset: -8px;
    outline-width: 8px;
    outline-color: #1a2b687d;
    ;
    font-family: "quadrangle"!important;
    src: url("http://atomiste.000webhostapp.com/css/fonts/quadrangle-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/quadrangle-webfont.woff") format("woff");
}

section {
    background-color: rgba(13, 47, 137, 0)!important;
}

.table > thead {
    background-color: rgba(49, 41, 53, .8);
}

.aside,
.adsbygoogle {
    display: none!important;
}

textarea,
input,
.form-control {
    background-color: rgba(11, 21, 45, .8)!important;
    font-family: "prototype";
    font-size: 18px!important;
    src: url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff2") format("woff2"),
    url("http://atomiste.000webhostapp.com/css/fonts/prototype-webfont.woff") format("woff");
}



.navbar,
.navbar-inverse .navbar-nav > .active > a,
.navbar-inverse .navbar-nav > .active > a:hover,
.navbar-inverse .navbar-nav > .active > a:focus {
    background-color: rgba(11, 21, 45, .8);
    border-bottom: 0px solid #121212!important;
}

.navbar-fixed-top {
    position: absolute!important;
}




.chat-box,
.bbcodes,
.footer {
    background-color: rgba(11, 21, 45, .8)!important;
    border-top: 0px solid #121212;
}

images > thread.png {
    content: url('https://nsa40.casimages.com/img/2020/01/12/mini_200112062634947111.png')!important;
}

.profile,
.preview {
    background-color: rgba(11, 21, 45, .8)!important;
}

/* AJOUT APRES MISE A JOUR 1.01 */

.pagination a {
    text-shadow: #000 1px 0 5px;
}

.glyphicon-chevron-right {
    margin-left:10px;
}

.glyphicon-chevron-left {
    margin-right:10px;
}

.profile-section {
    background-color: #f000!important;
}

}
