css:
@-moz-document domain("de.wikipedia.org") {
body {
    background-color: #111;
}
#mw-page-base {
    background-color: #151515;
    background-image: linear-gradient(to bottom, #000 50%, #111 100%);
}
#mw-panel .vector-menu-portal li a:visited, a:visited {
    color: #4b47d7;
}
#mw-panel .vector-menu-portal li a, a {
    color: #1d5fc9;
}
a.new, #p-personal a.new {
    color: #642828;
}
.portal h3 {
    background-image: linear-gradient(to right, transparent 0, #555 35%, #555 70%, transparent 100%);
}
.vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3 {
    background-image: linear-gradient(to bottom, rgba(85, 85, 85, 0) 0, #555 100%);
}
.vector-menu-tabs li,
.vector-menu-tabs li.selected {
    background-image: linear-gradient(to top, #555 0, #181818 1px, #000 100%);
}
.vector-menu .selected a, .vector-menu .selected a:visited {
    color: #aaa;
}
.vector-menu-tabs h3 {
    color: #555;
}
#searchInput {
    background-color: rgba(80, 80, 80, 0.5);
    border-color: #555;
    color: #777;
}
#searchInput:focus, #simpleSearch:hover #searchInput:focus {
    box-shadow: inset 0 0 0 1px #3366cc,inset 0 0 0 2px #282828;
}
.suggestions-special .special-label {
    color: #555;
}
.suggestions-result, .suggestions-special .special-query {
    color: #777;
}
.suggestions-results, .suggestions-special {
    background-color: #151515;
}
.suggestions-result-current {
    background-color: #2a4b8d;
}
.vector-menu-dropdown .vector-menu-content-list {
    background-color: #151515;
    border-color: #555;
}
.mw-body {
    border-color: #555;
}
.mw-body, .parsoid-body, .vectorTabs .selected {
    background-color: #111;
    color: #777;
}
h1, h2, h3, h4, h5, h6 {
    color: #999;
}
.oo-ui-panelLayout-framed {
    border-color: #555;
}
.oo-ui-icon-bell, .mw-ui-icon-bell::before {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22grey%22 viewBox=%220 0 20 20%22%3E%3Ctitle%3Ebell%3C/title%3E%3Cpath d=%22M16 7a5.38 5.38 0 0 0-4.46-4.85C11.6 1.46 11.53 0 10 0S8.4 1.46 8.46 2.15A5.38 5.38 0 0 0 4 7v6l-2 2v1h16v-1l-2-2zm-6 13a3 3 0 0 0 3-3H7a3 3 0 0 0 3 3z%22/%3E%3C/svg%3E");
}
.oo-ui-icon-tray, .mw-ui-icon-tray::before {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22grey%22 viewBox=%220 0 20 20%22%3E%3Ctitle%3Etray%3C/title%3E%3Cpath d=%22M3 1a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V3a2 2 0 0 0-2-2zm14 12h-4l-1 2H8l-1-2H3V3h14z%22/%3E%3C/svg%3E");
}
.oo-ui-icon-collapse, .mw-ui-icon-collapse::before {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22grey%22 viewBox=%220 0 20 20%22%3E%3Ctitle%3Ecollapse%3C/title%3E%3Cpath d=%22M2.5 15.25l7.5-7.5 7.5 7.5 1.5-1.5-9-9-9 9z%22/%3E%3C/svg%3E");
}
.oo-ui-icon-expand, .mw-ui-icon-expand::before {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22grey%22 viewBox=%220 0 20 20%22%3E%3Ctitle%3Eexpand%3C/title%3E%3Cpath d=%22M17.5 4.75l-7.5 7.5-7.5-7.5L1 6.25l9 9 9-9z%22/%3E%3C/svg%3E");
}
.vector-menu h3::after {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2212%22 height=%2212%22 fill=%22grey%22 viewBox=%220 0 12 12%22%3E%3Cpath d=%22M11.05 3.996l-.965-1.053-4.035 3.86-3.947-3.86L1.05 3.996l5 5 5-5%22/%3E%3C/svg%3E");
}
#mw-indicator-mw-helplink a {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22grey%22 viewBox=%220 0 20 20%22%3E%3Ctitle%3Ehelp%3C/title%3E%3Cpath d=%22M10 0a10 10 0 1 0 10 10A10 10 0 0 0 10 0zm1 16H9v-2h2zm2.71-7.6a2.64 2.64 0 0 1-.33.74 3.16 3.16 0 0 1-.48.55l-.54.48c-.21.18-.41.35-.58.52a2.54 2.54 0 0 0-.47.56A2.3 2.3 0 0 0 11 12a3.79 3.79 0 0 0-.11 1H9.08a8.9 8.9 0 0 1 .07-1.25 3.28 3.28 0 0 1 .25-.9 2.79 2.79 0 0 1 .41-.67 4 4 0 0 1 .58-.58c.17-.16.34-.3.51-.44a3 3 0 0 0 .43-.44 1.83 1.83 0 0 0 .3-.55 2 2 0 0 0 .11-.72 2.06 2.06 0 0 0-.17-.86 1.71 1.71 0 0 0-1-.9 1.7 1.7 0 0 0-.5-.1 1.77 1.77 0 0 0-1.53.68 3 3 0 0 0-.5 1.82H6.16a4.74 4.74 0 0 1 .28-1.68 3.56 3.56 0 0 1 .8-1.29 3.88 3.88 0 0 1 1.28-.83A4.59 4.59 0 0 1 10.18 4a4.44 4.44 0 0 1 1.44.23 3.51 3.51 0 0 1 1.15.65 3.08 3.08 0 0 1 .78 1.06 3.54 3.54 0 0 1 .29 1.45 3.39 3.39 0 0 1-.13 1.01z%22/%3E%3C/svg%3E");
}
.mw-ui-icon-preview-disambiguation::before {
	background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2220%22 height=%2220%22 fill=%22white%22 viewBox=%220 0 20 20%22%3E %3Ctitle%3E articles %3C/title%3E %3Cpath d=%22M5 0v2h11v14h2V2a2 2 0 0 0-2-2z%22/%3E %3Cpath d=%22M13 20a2 2 0 0 0 2-2V5a2 2 0 0 0-2-2H4a2 2 0 0 0-2 2v13a2 2 0 0 0 2 2zM9 5h4v5H9zM4 5h4v1H4zm0 2h4v1H4zm0 2h4v1H4zm0 2h9v1H4zm0 2h9v1H4zm0 2h9v1H4z%22/%3E %3C/svg%3E");
}
.mw-content-ltr .magnify a {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2215%22 height=%2211%22 viewBox=%220 0 11 15%22%3E%3Cg id=%22magnify-clip%22 fill=%22%23222%22 stroke=%22%23888%22%3E%3Cpath id=%22bigbox%22 d=%22M1.509 1.865h10.99v7.919H1.509z%22/%3E%3Cpath id=%22smallbox%22 d=%22M-1.499 6.868h5.943v4.904h-5.943z%22/%3E%3C/g%3E%3C/svg%3E");
}
.mw-parser-output .external {
    background-image: linear-gradient(transparent,transparent),url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%2212%22 height=%2212%22%3E%3Cpath fill=%22%23222%22 stroke=%22%2336c%22 d=%22M1.5 4.518h5.982V10.5H1.5z%22/%3E%3Cpath fill=%22%2336c%22 d=%22M5.765 1H11v5.39L9.427 7.937l-1.31-1.31L5.393 9.35l-2.69-2.688 2.81-2.808L4.2 2.544z%22/%3E%3Cpath fill=%22%23222%22 d=%22M9.995 2.004l.022 4.885L8.2 5.07 5.32 7.95 4.09 6.723l2.882-2.88-1.85-1.852z%22/%3E%3C/svg%3E");
}
ul {
	list-style-image: url("data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%225%22 height=%2213%22%3E%3Ccircle cx=%222.5%22 cy=%229%22 r=%222.5%22 fill=%22%23777%22/%3E%3C/svg%3E");
}
html .thumbimage {
    border-color: #555;
    opacity: .7;
}
div.thumbinner {
    background-color: #222;
    border-color: #333;
}
.toc, .mw-warning, .toccolours {
    background-color: #222;
    border-color: #333;
}
table > * > tr.hintergrundfarbe1 > th, table > * > tr > th.hintergrundfarbe1, table.hintergrundfarbe1, .hintergrundfarbe1 {
    background-color: #18191a;
}
.rahmenfarbe1 {
    border-color: #52555b;
}
div.NavHead, .mw-parser-output div.NavHead {
    background: #22252b;
}
code, pre, .mw-highlight {
    background-color: #222;
}
code, pre, .mw-code {
    border-color: #555;
    color: #888;
}
.mw-highlight .nc, .mw-highlight .nf {
    color: #3050FF;
}
.mw-parser-output div.NavFrame {
    border-color: #52555b;
}
.catlinks {
    background-color: #18191a;
    border-color: #52555b;
}
.catlinks li {
    border-color: #555;
}
fieldset {
    border-color: #444;
}
.flaggedrevs_reviewform {
    background-color: #18191a;
}
.flaggedrevs_reviewform legend {
    color: #555;
}
#footer li {
    color: #444;
}

.mwe-popups {
    background: #222;
}
.mwe-popups .mwe-popups-extract {
    color: #777;
}
.mwe-popups.flipped-x-y::before,
.mwe-popups.flipped-x-y.mwe-popups-is-tall::before,
.mwe-popups.flipped-y::before {
    border-top-color: #000;
}

.mwe-popups.flipped-x-y::after,
.mwe-popups.flipped-x-y.mwe-popups-is-tall::after,
.mwe-popups.flipped-y::after {
    border-top-color: #222;
}

.mwe-popups.mwe-popups-image-pointer::before,
.mwe-popups.mwe-popups-image-pointer.flipped-x::before,
.mwe-popups.flipped-x.mwe-popups-is-tall::before {
    border-bottom-color: #000;
}
.mwe-popups.mwe-popups-no-image-pointer::after,
.mwe-popups.mwe-popups-image-pointer::after,
.mwe-popups.mwe-popups-image-pointer.flipped-x::after {
    border-bottom-color: #222;
}
.mwe-popups .mwe-popups-extract[dir='ltr']::after {
    background-image: linear-gradient(to right, rgba(34, 34, 34, 0) 0, #222 50%);
}
.mwe-popups .mwe-popups-extract[dir='rtl']::after {
    background-image: linear-gradient(to left, rgba(34, 34, 34, 0) 0, #222 50%);
}

.mwe-popups-overlay {
    background-color: rgba(38, 38, 38, 0.9);
}
.mwe-popups.mwe-popups-type-reference .mwe-popups-extract .mwe-popups-fade {
    background-image: linear-gradient(rgba(34, 34, 34, 0), #222);
}
.wikitable {
    background-color: #222;
    border-color: #555;
    color: #777;
}
.wikitable > tr > th,
.wikitable > tr > td,
.wikitable > * > tr > th,
.wikitable > * > tr > td {
    border-color: #555;
}
.wikitable > tr > th,
.wikitable > * > tr > th {
    background-color: #333;
}
table.wikitable.zebra > tbody > :nth-child(2n):not([class*="hintergrundfarbe"]) {
    background-color: #151515;
}
sup.reference:target {
    background-color: #ccc;
}
ol.references li:target {
    background-color: #282828;
}
.mw-searchresults-has-iw .iw-resultset {
    background-color: #222;
}
.mw-parser-output .hauptseite-box-title {
    background-color: #22252b;
    border-color: #555;
}
.mw-parser-output .hauptseite-box-content {
    background-color: #111;
    border-color: #555;
}
.mw-history-legend > div {
    background-color: #111 !important;
    border-color: #555 !important;
}
#pagehistory li.selected {
    background-color: #222;
    outline-color: #555;
    color: #999;
}
.flaggedrevs-color-0 {
    background-color: #282828;
}
.flaggedrevs-color-1 {
    background-color: #235;
}
.flaggedrevs-color-2 {
    background-color: #283811;
}
.flaggedrevs-color-3, .flaggedrevs-unreviewed {
    background-color: #331;
}
.flaggedrevs-pending {
    background-color: #443;
}
textarea {
    background-color: #282828;
    color: #777;
    border-color: #555;
}
.warningbox {
    background-color: #282810;
    border-color: #860;
    color: #aaa;
}
.editOptions {
    background-color: #282828;
    border-color: #555;
    color: #777;
}
div#editpage-copywarn {
    background-color: #111;
}
.mw-wiki-logo, .mwe-math-fallback-image-inline {
    filter: invert();
    opacity: .5;
}
}
