css:
@-moz-document domain("bbs.hupu.com") {
div,
p,
li {
    font-family: "Hiragino Sans GB", "Microsoft YaHei",
    "WenQuanYi Micro Hei", sans-serif !important;
}
.titlelink.box a {
    font-size: 160%;
}
small {
    font-size: 12px !important;
}
.case small {
    font-size: 12px !important;
}
.case,
.case * {
    font-size: 22px !important;
    line-height: 2.2 !important;
}
.case small a {
    font-size: 12px !important;
    color: #ccc !important;
}
.bbsHotPit .show-list .for-list li .titlelink {
    line-height: 2;
    padding: 15px 0;
    font-size: 13px;
}
body .hp-topbarNav-bd,
body .hp-header,
body .hp-wrap,
body .hp-footer,
body .hp-w,
body .hp-threeNav,
body .f_re,
body .bbs-content {
    width: 1400px;
}
body .bbs-content #container .container_padd .bbsHotPit {
    width: 1220px !important;
}
.details #container #container_nopadd #sub_line #t_main,
.w_reply {
    width: 1090px !important;
}
.bbsHotPit .show-list .show-tit .t1,
.bbsHotPit .show-list .for-list li .titlelink {
    width: 860px !important;
}
.showPlate li a,
.feedhome li,
.feedhome li *,
.hp-threeNav .hp-threeNav-bd .hp-threeNav-item li a,
.hp-mainNav-B .hp-nav a,
.hp-mainNav-B .hp-nav a:active,
.hp-mainNav-B .hp-nav a:hover,
.hp-mainNav-B .hp-nav a:visited {
    font-size: 16px !important;
    line-height: 1.8 !important;
}
.showPlate .bbs-showMyPlate li .selected,
.showPlate .bbs-showMyPlate li .selected {
    background: #fff none;
    ;
}
.showPlate .bbs-showMyPlate li a .num,
.bbsHotPit #sort,
.bbsHotPit .bbs_head .pl_nav,
.bbsHotPit .tabs_header .left {
    display: none;
}
.showPlate a,
.showPlate .forMyplate {
    padding-left: 10px !important;
    margin: 0 !important;
    width: 126px !important;
}
.showPlate .forMyplate {
    padding: 10px 0;
}
.showPlate .morebbs {
    margin-top: 10px;
}
}
