css:
@-moz-document domain("krajwspanialy.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #B21100 #1c1c1c;
}
a{
    color: #B21100;
}
/*HEADER*/
#wi-mainnav .menu > ul > li > a{
    color: #B21100;
}
#wi-mainnav .menu > ul > li > a:hover{
    color: #0F425F;
}
#wi-mainnav .menu > ul > li.current-menu-item > a{
    background: #B21100;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
#wi-wrapper{
    background-color: #1c1c1c;
}
.grid-meta,.grid-content p,.post-navigation .nav-links a .meta-nav{
    color: #fff;
}
.grid-cats a,.grid-title a,.readmore,.wi-pagination a.page-numbers{
    color: #B21100;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.post-title,.meta-category a,.widget_recent_comments ul li a,.widget_recent_entries ul li a{
    color: #B21100;
}
.meta-time,.meta-category{
    color: #fff;
}
.widget-title{
    background: #B21100;
}
.post-navigation .nav-links .post-title:hover{
    color: #db4a37;
}
blockquote a{
    color: #0F425F;
}
ul.related_post li a:hover,.single-body a:hover{
    text-decoration: underline !important;
}
/*KOMENTARZE*/
input[type="text"], input[type="email"], input[type="number"], input[type="url"], input[type="password"], input[type="tel"], textarea{
    color: #fff;
}
#respond input[type="text"], #respond input[type="email"], #respond input[type="url"], #respond textarea{
    border: 1px solid #B21100;
}
input[type="text"]::placeholder, input[type="email"]::placeholder, input[type="number"]::placeholder, input[type="url"]::placeholder, input[type="password"], input[type="tel"]::placeholder,textarea::placeholder{
    color: #fff !important;
}
#respond #submit{
    background: #B21100;
}
#respond #submit:hover{
    background: #0F425F;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
/*WYSZUKIWARKA*/
.searchform .s{
    color: #fff;
    border: 1px solid #B21100;
}
.searchform .s::placeholder{
    color: #fff;
}
.searchform .submit{
    color: #B21100;
}
/*WYSZUKIWARKA*/
/*KONTAKT*/
label{
    color: #fff
}
.wpcf7 .wpcf7-submit{
    background: #B21100;
}
.wpcf7 .wpcf7-submit:hover{
    background: #0F425F;
}
/*KONTAKT*/
/*PODSTRONY*/
/*FOOTER*/
.copyright:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #0F425F;
}
/*FOOTER*/
}
