css:
/* ==UserStyle==
@name           暗调菜鸟教程 | Darkoob | Dark Runoob | 黑夜也要奔跑
@namespace      https://github.com/li-zyang
@version        1.0.4
@description    给菜鸟教程和菜鸟工具加一个暗色主题\nDark style for Runoob Tutorial and Runoob Tools
@author         li-zyang
==/UserStyle== */
@-moz-document 
  domain("runoob.com"), 
  url-prefix("https://www.runoob.com/try/") {
  :root {
    --bg1: #202020;
    --bg2: #2b2c2a;
    --bg3: #333333;
    --header-bg: #36402f;
    --default-fg: #e0e0e0;
    --dark-fg: #949292;
    --title-fg: #9b9b9b;
    --link-unvisited-fg:#849974;
    --split-line-fg: #777;
    --button-fg-grey: #ccc;
    --button-fg-green: #2c882c;
    --button-fg-green-darkened: #236523;
    --img-opacity: 0.7;
    --code-tag: #2b0;
    --code-bracket: #db8;
    --code-attribute: #77d;
    --code-string: #d33;
    --code-keyword: #d7e;
    --code-atom: #aac;
    --code-number: #9cb;
    --code-variable: #0fe;
    --code-property: #7ec;
    --code-comment: #777;
    --code-builtin: #2b0;
    --code-def: #acf;
    --code-punc: #7cc;
    --code-special: #f95;
  }
  html, body {
    background: var(--bg1);
    color: var(--default-fg);
  }
  *:not(iframe) div {
    background: transparent;
    color: var(--default-fg);
  }
  *:not(iframe) table
  *:not(iframe) ul,
  *:not(iframe) ol,
  *:not(iframe) li,
  *:not(iframe) td,
  *:not(iframe) tr,
  *:not(iframe) th {
    background: transparent;
    color: var(--default-fg);
  }
  *:not(iframe) select {
    color: var(--default-fg) !important;
  }
  *:not(iframe) pre {
    font-family: Menlo,Monaco,Consolas,"Courier New",monospace !important;
  }
  *:not(iframe) img {
    opacity: 0.8;
    transition: 0.5s;
  }
  *:not(iframe) img:hover {
    opacity: 1;
  }
  .list-tool .media-body {
    color: var(--dark-fg);
  }
  .media h4 a {
    color: var(--link-unvisited-fg);
  }
  .list-row {
    background: var(--bg2);
  }
  .list-tool img {
    opacity: var(--img-opacity);
    border-radius: 3px;
  }
  .list-tool {
    background-color: var(--bg2);
    border: 0px;
  }
  .runoob-header {
    background-color: var(--header-bg);
    border-color: transparent;
  }
  .navbar-inverse .navbar-toggle {
    border-color: transparent;
  }
  .navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form {
    border-color: transparent;
    border-radius: 3px;
  }
  .navbar-form {
    box-shadow: none;
  }
  .form-control {
    background-color: var(--header-bg);
  }
  .btn-default {
    color: var(--button-fg-grey);
    background-color: var(--header-bg);
    border-color: var(--button-fg-grey);
  }
  .page-header {
    border-bottom: 1px solid var(--split-line-fg);
  }
  .navbar .navbar-default .navbar-fixed-top {
    background: var(--header-bg) !important;
  }
  .panel-default > .panel-heading {
    color: var(--default-fg) !important;
    background-color: var(--bg2) !important;
    border-color: transparent !important;
  }
  .panel {
    background-color: var(--bg2) !important;
    border: 1px solid transparent !important;
    border-radius: 4px;
    box-shadow: none;
  }
  .panel-default {
    border-color: transparent !important;
  }
  .CodeMirror {
    color: var(--default-fg) !important;
    background: var(--bg1) !important;
  }
  .CodeMirror-cursor {
    border-left: 2px solid var(--default-fg) !important;
  }
  .cm-tag {
    color: var(--code-tag) !important; 
  }
  .cm-bracket {
    color: var(--code-bracket) !important;
  }
  .cm-attribute {
    color: var(--code-attribute) !important;
  }
  .cm-string {
    color: var(--code-string) !important;
  }
  .cm-keyword {
    color: var(--code-keyword) !important;
  }
  .cm-atom {
    color: var(--code-atom) !important;
  }
  .cm-number {
    color: var(--code-number) !important;
  }
  .cm-variable {
    color: var(--code-variable) !important;
  }
  .cm-property {
    color: var(--code-property) !important;
  }
  .cm-comment {
    color: var(--code-comment) !important;
  }
  .cm-builtin {
    color: var(--code-builtin) !important;
  }
  .cm-def {
    color: var(--code-def) !important;
  }
  .cm-operator {
    color: var(--code-punc) !important;
  }
  .cm-special {
    color: var(--code-special) !important;
  }
  .hl-tag {
    color: var(--code-tag) !important; 
  }
  .hl-bracket {
    color: var(--code-bracket) !important;
  }
  .hl-brackets {
    color: var(--code-bracket) !important;
  }
  .hl-attribute {
    color: var(--code-attribute) !important;
  }
  .hl-string {
    color: var(--code-string) !important;
  }
  .hl-keyword {
    color: var(--code-keyword) !important;
  }
  .hl-reserved {
    color: var(--code-keyword) !important;
  }
  .hl-atom {
    color: var(--code-atom) !important;
  }
  .hl-number {
    color: var(--code-number) !important;
  }
  .hl-variable {
    color: var(--code-variable) !important;
  }
  .hl-property {
    color: var(--code-property) !important;
  }
  .hl-identifier {
    color: var(--code-property) !important;
  }
  .hl-comment {
    color: var(--code-comment) !important;
  }
  .hl-builtin {
    color: var(--code-builtin) !important;
  }
  .hl-def {
    color: var(--code-def) !important;
  }
  .hl-code {
    color: var(--code-punc) !important;
  }
  .hl-var {
    color: var(--code-varible) !important;
  }
  .hl-special {
    color: var(--code-special) !important;
  }
	pre .pun {
    color: var(--code-punc) !important;
	} 
	pre .pln {
    color: var(--code-variable) !important;
	} 
	pre .str {
    color: var(--code-string) !important;
	}
  pre .lit {
    color: var(--code-number) !important;
  }
  pre .tag {
    color: var(--code-tag) !important;
  }
  pre .atn {
    color: var(--code-attribute) !important;
  }
  pre .atv {
    color: var(--code-varible) !important;
  }
  pre .dec {
    color: var(--code-def) !important;
  }
  pre .kwd {
    color: var(--code-keyword) !important;
  }
  pre .com {
    color: var(--code-comment) !important;
  }
  pre .typ {
    color: var(--code-keyword) !important;
  }
  .navbar {
    background: var(--header-bg) !important;
    border-color: transparent !important;
  }
  .btn-success:hover {
    color: var(--dark-fg) !important;
    background-color: var(--button-fg-green-darkened) !important;
    border-color: transparent !important;
  }
  .btn-success {
    color: var(--default-fg) !important;
    background-color: var(--button-fg-green) !important;
    border-color: transparent !important;
  }
  .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber, .CodeMirror-scroll, .CodeMirror-sizer {
    -moz-box-sizing: content-box;
    box-sizing: content-box;
    background: var(--bg1);
    border-color: var(--split-line-fg);
  }
  .CodeMirror {
    border-color: transparent !important;
  }
  #compiler-textarea-result {
    background: var(--bg1);
    border-color: transparent;
    color: var(--default-fg);
  }
  .dropdown-menu {
    background: var(--bg2) !important;
  }
  .dropdown-menu a {
    color: var(--default-fg) !important;
  }
  .adsbygoogle {
    display: none;
  }
}
@-moz-document 
url-prefix("https://c.runoob.com/front-end/") {
  pre {
    background: var(--bg1) !important;
    color: var(--default-fg) !important;
    border-color: transparent !important;
  }
  tr {
    background: var(--bg1) !important;
    color: var(--default-fg) !important;
  }
  select, option {
    background: var(--bg1) !important;
    border: 1px solid var(--split-line-fg);
  }
  input {
    background: transparent !important;
    color: var(--default-fg) !important;
    border: 1px solid var(--split-line-fg);
  }
  textarea {
    background: transparent !important;
    border: 1px solid var(--split-line-fg);
    border-radius: 3px;
    padding: 0px 8px 0px 8px;
  }
  button, input[type=button] {
    background: var(--bg3);
    border: none;
    border-radius: 3px;
    overflow: hidden;
    transition: 0.5s;
  }
  button:hover, input[type=button]:hover {
    background: var(--bg1);
  }
  .panel-footer {
    opacity: 0;
  }
}
@-moz-document 
regexp("https?://c.runoob.com/(?!(front-end/|compile/)).*") {
  *:not(iframe) div {
    background: transparent !important;
    color: var(--default-fg) !important;
  }
  *:not(iframe) ul,
  *:not(iframe) ol,
  *:not(iframe) li,
  *:not(iframe) td,
  *:not(iframe) tr,
  *:not(iframe) th {
    background: transparent !important;
    color: var(--default-fg) !important;
  }
  *:not(iframe) li,
  *:not(iframe) td{
    transition-duration: 0.5s;
    border-color: transparent !important;
  }
  *:not(iframe) li:hover,
  *:not(iframe) td:hover {
    background: var(--bg2) !important;
    color: var(--default-fg) !important;
    border-color: transparent !important;
  }
  *:not(iframe) select {
    color: var(--default-fg) !important;
  }
  *:not(iframe) pre {
    font-family: Menlo,Monaco,Consolas,"Courier New",monospace !important;
  }
  *:not(iframe) li[class~=active],
  *:not(iframe) td[class~=active] {
    background: var(--bg2) !important;
    color: var(--default-fg) !important;
  }
  *:not(iframe) ul a,
  *:not(iframe) table a,
  *:not(iframe) ol a {
    background: transparent !important;
    border: 1px solid transparent !important;
    color: var(--link-unvisited-fg);
  }
  .nav-tabs {
    border-bottom: none;
  }
}
@-moz-document 
  domain("www.runoob.com") {
  .article {
    background: var(--bg2) !important;
    border-color: transparent;
    color: var(--default-fg);
  }
  .sidebar-box {
    background: var(--bg2);
    color: var(--default-fg);
  }
  .sidebar-box a {
    background: transparent;
    border-color: transparent !important;
    color: var(--dark-fg) !important;
  }
  .sidebar-box h1,
	.sidebar-box h2,
	.sidebar-box h3,
	.sidebar-box h4,
	.sidebar-box h5,
	.sidebar-box h6 {
    background: transparent !important;
  }
  .article hr {
    visibility: hidden;
  }
	.article-intro h1 {
    color: var(--title-fg) !important;
    font-weight: 1000;
  }
	.article-intro h2, 
	.article-intro h3, 
	.article-intro h4, 
	.article-intro h5, 
	.article-intro h6 {
    color: var(--title-fg) !important;
  }
  .previous-next-links {
    background: var(--header-bg);
    border-color: transparent;
  }
  .previous-next-links a {
    color: var(--button-fg-grey);
  }
  div.example {
    background-color: var(--bg3) !important;
    background-image: none !important;
    color: var(--default-fg);
    border: 1px solid transparent;
    width: auto;
    margin: 8px 0px 8px 0px;
  }
  div.example_code {
    background-color: var(--bg1) !important;
    border: none;
    border-radius: 3px;
  }
  .hl-main {
    background-color: transparent !important;
  }
  a.showbtn, a.showbtn:link, a.showbtn:visited, a.tryitbtn, a.tryitbtn:link, a.tryitbtn:visited {
    color: var(--default-fg);
    background-color: var(--button-fg-green-darkened);
    border: 1px solid transparent;
  }
  .comment-signarea {
    background: transparent !important;
  }
  .comt-box {
    border: 2px solid transparent;
    background-color: var(--bg2);
  }
  .simditor .simditor-wrapper {
    background: transparent !important;
  }
  .simditor {
    border: 1px solid var(--split-line-fg) !important;
  }
  .simditor .simditor-toolbar {
    border-bottom: none !important;
    background: var(--bg3) !important;
    border-right: 1px solid var(--split-line-fg) !important;
  }
  .simditor .simditor-toolbar > ul > li > .toolbar-item {
    color: var(--default-fg) !important;
  }
  .simditor .simditor-body p, .simditor .simditor-body div, .editor-style p, .editor-style div {
    color: var(--default-fg);
  }
  .simditor .simditor-body pre, .editor-style pre, .comt-main pre
  .prettyprint, .prettyprinted {
    background: var(--bg1);
    border-radius: 3px !important;
    color: var(--default-fg);
    border: 0px solid transparent;
    border-left-width: 4px;
    overflow: hidden;
    border-left-color: var(--dark-fg);
  }
  .simditor .simditor-toolbar > ul > li > .toolbar-item.active {
    background: var(--bg1) !important;
  }
  #submit {
    right: 0px;
    background: var(--button-fg-green-darkened);
    border-color: transparent;
    color: var(--default-fg);
  }
  .comt-comterinfo input {
    border: 1px solid transparent !important;
    background: var(--bg3) !important;
    color: var(--default-fg) !important;
  }
  .comt-ctrl {
    background-color: var(--bg3) !important;
  }
  #footer .copyright {
    background: var(--bg1);
    box-shadow: 0 -1px 1px transparent;
  }
  .feedback-btn {
    background: var(--bg3);
    color: var(--dark-fg) !important;
  }
  .feedback-btn-gray:hover {
    background-color: var(--bg3) !important;
    border-color: transparent;
  }
  .search input {
    background-color: var(--bg1) !important;
    border: 1px solid transparent;
    color: var(--dark-fg) !important;
  }
  .search form {
    background: var(--bg2) !important;
  }
  .navigation {
    background: transparent;
  }
  .nav li a {
    color: var(--dark-fg);
  }
  .fixed-btn {
    border: none;
    background-color: var(--button-fg-grey);
    border-radius: 3px;
    overflow: hidden;
  }
  .fixed-btn .go-top {
    border-bottom: none;
  }
  .tab {
    color: var(--title-fg);
    border: none;
    background-color: var(--bg3);
    background-image: none;
  }
  .right-list button {
    opacity: 0.9;
  }
  .sidebar-box.sidebar-cate {
    background-color: var(--bg2);
  }
  .sidebar-tree {
    border: 1px solid transparent;
  }
  .sidebar-tree li {
    border: none !important;
  }
  .sidebar-tree li.selected, .sidebar-tree .h2-tit {
    background-color: var(--bg3) !important;
  }
  .sidebar-tree ul li ul {
    background-color: var(--bg3) !important;
  }
  .sidebar-tree ul li ul li{
    border-color: transparent !important;
  }
  .right-column > div:nth-last-of-type(1) {
    display: none;
  }
  #footer .runoob-block {
    background: var(--bg1) !important;
  }
  #footer .runoob-block {
    background: var(--bg1);
    border-top: 1px solid var(--split-line-fg);
    border-bottom: none;
  }
  #footer .runoob dl {
    color: var(--dark-fg);
    border-left: 1px solid transparent;
  }
  #footer .runoob dl dt {
    color: var(--dark-fg);
    font-weight: 900;
  }
  #footer .runoob a {
    color: var(--dark-fg);
  }
  #footer .search-share {
    border-left: none;
  }
  .modal {
    background-color: var(--bg3) !important;
    border: 1px solid transparent;
  }
  .fixed-btn .modal.fade.in::before {
    border-left: 9px solid transparent;
  }
  .fixed-btn .modal.fade.in::after, .fixed-btn .modal.fade.in::before {
    border-top: 9px solid transparent;
    border-bottom: 9px solid transparent;
    border-left: 9px solid var(--bg3);
  }
  .middle-column-home {
    border: 1px solid transparent;
    background-color: var(--bg2);
  }
  .codelist a.item-top {
    color: var(--default-fg);
    background-color: var(--bg3);
  }
  .codelist a.item-top:hover {
    color: var(--dark-fg);
    background-color: var(--bg1);
  }
  .codelist a.item-top strong {
    color: var(--dark-fg);
  }
  .codelist h2 {
    border-bottom: none;
    color: var(--dark-fg);
  }
  .design:nth-child(2n) {
    background: var(--bg2);
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
  }
  blockquote {
    background-color: var(--bg3);
    margin: 10px 0px 10px 0px;
  }
  .article-body .quote, .article-body blockquote p {
    background: transparent;
  }
  .article-body a {
    color: var(--link-unvisited-fg);
  }
  #comments h2 {
    background-color: var(--bg3);
    color: var(--title-fg);
  }
  #comments .altblock {
    color: var(--default-fg);
    background-color: transparent;
  }
  .comt-main {
    color: var(--default-fg);
  }
  .comt-avatar {
    background-color: transparent;
  }
  .comt-meta {
    color: var(--dark-fg);
  }
  .wrapper {
    background: var(--bg3);
    color: var(--default-fg);
  }
  .marked {
    background-color: var(--bg1);
  }
  table.reference tr {
    background-color: transparent !important;
  }
  table.reference th {
    color: var(--default-fg);
    background-color: var(--bg3);
    border: 1px solid var(--split-line-fg);
  }
  table.reference td {
    border: 1px solid var(--split-line-fg);
  }
  div.tryit_ex img {
    display: none;
  }
  .lamp img {
    border-radius: 16px;
    overflow: hidden;
  }
  table.lamp th {
    color: var(--default-fg);
    background-color: var(--bg3);
  }
  :not(iframe) table :not(iframe) ul, :not(iframe) ol, :not(iframe) li, :not(iframe) td, :not(iframe) tr, :not(iframe) th {
    background: transparent;
    color: var(--default-fg);
  }
  table.lamp td {
    padding: 4px;
    padding-left: 0;
    padding-right: 10px;
    background-color: var(--bg3);
  }
  table.lamp {
    width: 100%;
    padding: 0;
    border: 1px solid var(--bg3);
  }
  .comment {
    border-bottom: var(--bg1) 1.5px solid;
  }
  div.example_result {
    background-color: var(--bg1);
    border: none;
    border-radius: 3px;
    overflow: hidden;
    color: var(--default-fg);
  }
  h2.left {
    color: var(--title-fg);
  }
  table.browserref tr:nth-child(2n+1) {
    background-color: var(--bg3);
  }
  table.browserref th {
    border: 1px solid var(--split-line-fg);
    background-color: var(--bg3);
    color: var(--default-fg);
  }
  table.browserref tr:nth-child(2n) {
    background-color: var(--bg2);
  }
  table.browserref td {
    border: 1px solid var(--split-line-fg);
  }
  div.code {
    border: 1px solid transparent;
    background-color: var(--bg1);
    color: var(--default-fg);
    border-radius: 3px;
    overflow: hidden;
  }
  .answers a {
    background: transparent;
    color: var(--default-fg) !important;
  }
  .article-heading {
    background: var(--bg2);
    border-bottom: 1px solid var(--split-line-fg);
  }
  .logo > h1
  {
    background-image: url("data:image/png;base64,");
  }
  #leftcolumn a[style] {
    background: var(--dark-fg) !important;
    color: var(--bg3) !important;
  }
  #leftcolumn {
    padding: 4px 0px 4px 0px;
  }
  #leftcolumn a {
    padding-left: 8px;
    padding-right: 8px;
  }
  .archive-options {
    background: transparent;
    border: 1px solid transparent;
  }
  .archive-list {
    background: var(--bg2);
    border: 1px solid transparent;
  }
  .archive-list-item:nth-child(2n) {
    background: var(--bg2);
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
  }
  #wp_page_numbers li a {
    color: var(--dark-fg);
    background-color: transparent;
    background-image: none !important;
    border-color: transparent;
  }
  #wp_page_numbers li span {
    color: var(--dark-fg);
    background-color: var(--bg1);
    background-image: none !important;
    border-color: transparent;
  }
}
