css:
@-moz-document domain('kurnii.fi') {

html
{
  overflow-y: scroll;
}

html, body
{
  color: white;
  background-color: #2C3531;
  font-family: Roboto, sans-serif;
  font-size: 14px;
  margin: 0;
  padding: 0;
  text-align: center;
}

@media (max-width: 599px)
{
  #container
  {
    width: 100%;
  }

  #container
  {
    margin-left: auto;
    margin-right: auto;
    width: 700px;
  }
}

#lists
{
  box-shadow: 0 50px 40px rgba(0,0,0,0.2);
}

#menu
{
  background-color: #000;
  border: none;
  border-bottom-style: solid;
  border-color: rgba(255,255,255,0.5);
  border-width: 0px;
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
  -webkit-flex-direction: row;
  -ms-flex-direction: row;
  flex-direction: row;
  height: 45px;
  margin: 10px;
}

button
{
  -webkit-box-flex: 1;
  -webkit-flex: 1;
  -ms-flex: 1;
  flex: 1;
  background-color: transparent;
  border: none;
  border-color: rgba(255,255,255,0.3);
  border-radius: 0;
  border-right-style: solid;
  border-width: 2px;
  color: #fff;
  font-family: Roboto, sans-serif;
  font-size: 1.1em;
  font-weight: 700;
  padding: 4px;
  -webkit-tap-highlight-color: transparent;
  -webkit-transition: background-color .4s ease-out;
  transition: background-color .4s ease-out;
}

button:active
{
  background-color: rgba(255,255,255,0.5);
  outline: 0;
}

button:focus
{
  background-color: rgba(255,255,255,0.3);
  outline: 0;
}

button:hover
{
  background-color: rgba(255,255,255,0.2);
  cursor: pointer;
}

button:last-child
{
  border-right-style: none;
}

button.selected
{
  background-color: rgba(255,255,255,0.3);
}

.hidden
{
  display: none;
}

.place
{
  background-color: #272727;
  color: #2E9CCA;
  cursor: pointer;
  font-size: 20px;
  padding: 6px 0;
}

.food
{
  border-bottom: 1px solid #272727;
  padding: 10px;
}

.food:last-child
{
  border-bottom: none;
}

#footer
{
  font-size: 0.7em;
  padding: 15px 0;
}
}
