css:
@-moz-document domain("trakt.tv") {
html {
    scrollbar-color: #666 #212121;
    scrollbar-width: thin;
    overflow-y: overlay;
}

html::-webkit-scrollbar {
    background-color: transparent;
    width: 10px;
}
html::-webkit-scrollbar-thumb {
    background: #777;
}
html::-webkit-scrollbar-thumb:hover {
    background: #999;
}

#hu-ck-st-er-desk-top-sidenav,
    #hu-ck-st-er-desk-top-wrapper,
    #hu-ck-st-er-mob-ile-square-wrapper,
    .hu-ck-st-er-grid-item {
        height: 0px!important;
        width: 0px!important;
        overflow: hidden!important;
        margin: 0!important;
        padding: 0!important;
        display: none!important;
    }

.users-wrapper,
.summary-activity .tabs .tab,
.summary-activity .tabs,
#progress-wrapper .row .main-info .seasons,
.right.icons .comment-wrapper,
section,
#ondeck-wrapper,
#charts-wrapper,
#activity-wrapper,
#network-wrapper .alert-no-data,
#recommendations-wrapper {
    background-color: #212121!important;
    color: #eee!important;
    border-color: #666!important;
}

#seasons-episodes-sortable .under-info .titles,
#info-wrapper .season-links,
.summary-activity .tabs .tab.selected,
.comment-wrapper .above-comment,
header#top-nav #header-search #header-search-query.open,
#standard-features,
#standard-features .container,
.popover,
.popover-title,
.popover select,
.pagination-top ul.pagination li a,
.pagination-bottom ul.pagination li a,
#watch-now-modal,
.streaming-links,
.streaming-links .section,
#info-wrapper .sidebar .external li a,
.comment-wrapper,
.dropdown-menu,
#info-wrapper .affiliate-links .section a,
#schedule-wrapper,
#network-wrapper {
    background-color: #333!important;
    color: #eee!important;
    fill: #eee!important;
}

#standard-features {
    background: #333!important;
}

.pagination-top ul.pagination li a:hover,
#info-wrapper .affiliate-links .section a:hover {
    background-color: #ccc!important;
    color: #555!important;
}

#actors .tab-links h3:hover .count,
.posters .poster,
.posters > div .quick-icons{
    border-color: #555!important;
}

#seasons-episodes-sortable ul.additional-stats .unknown,
#info-wrapper ul.additional-stats li label,
#info-wrapper .season-links .links ul li a,
#actors .tab-links h3,
.comment-wrapper .above-comment .date,
#standard-features h5,
.streaming-links .title,
.custom-list .list-info .user-actions .icon.btn-list-clone,
.custom-list .list-info .user-actions .icon.btn-list-subscribe,
.comment-wrapper.list .above-comment .user-name h4 a.username,
#info-wrapper .affiliate-links .section .title,
#info-wrapper .sidebar .sections li a,
#info-wrapper .info .actors-wrapper .posters ul li .name,
#info-wrapper .info .actors-wrapper .posters ul li .count,
#charts-wrapper h3,
.genre-bars .bar label .count,
.genre-bars .bar label .count a,
#recommendations-wrapper .grid-item h4 a,
#ondeck-wrapper .posters .titles-link {
    color: #bbb!important;
}

#seasons-episodes-sortable .under-info h3 a,
#info-wrapper .season-links .links ul li a.selected,
#actors .tab-links h3:hover .count,
#actors .tab-links h3:hover,
#credits .tab-links h3:hover,
.dropdown-menu > li > a,
#info-wrapper .sidebar .sections li a:hover,
#info-wrapper .sidebar .sections li a:focus,
#info-wrapper .info .actors-wrapper .posters ul li .character,
#ondeck-wrapper .posters > div .titles h3,
body:not(.dark-knight) .genre-bars.light .bar label,
#recommendations-wrapper .grid-item h3 {
    color: #fefefe!important;
}

.dropdown-toggle {
    background-color: transparent!important;
    color: #eee!important;
}

.users-wrapper {
    border: none!important;
    border: 2px solid #666!important;
}

.quick-icons .actions > a.watch-now:hover .base, .quick-icons .actions > a.watch-now.selected .base{
    background: transparent!important;
}
.quick-icons .actions > a.watch-now:hover .base, .quick-icons .actions > a.watch-now.selected .base:hover {
    background: #555!important;
}

.btn-watch,
.btn-collect,
.btn-list,
.btn-comment {
    background-color: #212121!important;
}

.btn-collect:hover {
    background-color: #16a085!important;
}

.btn-comment:hover {
    background-color: #6e7a83!important;
}

.btn-list:hover {
    background-color: #009edf!important;
}

.btn-watch:hover {
    background-color: #8e44ad!important
}

#info-wrapper .sidebar .streaming-links,
#info-wrapper .sidebar .btn-watch-now {
    box-shadow: none;
    -webkit-box-shadow: none;
}

#actors .active h3,
#info-wrapper .season-links .links ul li a:hover {
    color: #ed1c20!important
}

A{
    color: #f93539;
}

#seasons-episodes-sortable .under-info h3 a:hover, #seasons-episodes-sortable .under-info h3 a:focus {
    color: #f93539!important;
}

#seasons-episodes-sortable .under-info .overview[data-readmore][aria-expanded="false"] .shade,
#info-wrapper .info #overview[data-readmore][aria-expanded="false"] .shade {
    background-image: -webkit-linear-gradient(top, rgba(243, 243, 243, 0) 0%, #212121 100%);
    background-image: -o-linear-gradient(top, rgba(243, 243, 243, 0) 0%, #212121 100%);
    background-image: linear-gradient(to bottom, rgba(243, 243, 243, 0) 0%, #212121 100%);
    background-repeat: repeat-x;
    filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr='#00F3F3F3', endColorstr='#212121', GradientType=0)";
}
}
