css:
/*
 Based on 
 HS Dark 2019
 by Pääsuke
 https://userstyles.org/styles/173992/hs-dark-2019
*/
@-moz-document domain(hs.fi) {
/*
 Based on 
 HS Dark 2019
 by Pääsuke
 https://userstyles.org/styles/173992/hs-dark-2019
*/
html, body, .bg-hs-pearl {
    background-color:  #19151e;
}

.content-wrapper {
    background-color:  #2a212e;
}
/* Front page background */
.teaser.feature .teaser-category ,
.main-navigation,
.teaser .teaser-text-wrapper, .articlepack-item.full, .teaser .teaser-links, .most-recent, .most-read, .right-position .is-list .vignette, .is-list .content, .is-list .is-most-read-articles-list .rank, .most-recent .is-most-recent-articles-list .timestamp, .is-list, .supersaa-widget.theme-hs .supersaa-widget-content, .live-schedule-content, .live-schedule, .hsnl-container, .breaking-news ul > li a, .hsnl-container {
	background-color: #2f2533;
}
.bg-white  {
	background-color: #2f2533;
    color: #e3e3e3;
}
.teaser.feature .teaser-category {
    color: #92cfd9;
}


/* "Juuri nyt" label in breaking news */
.breaking-news ul > li .label {
    color: white;
}

/* Uusimmat */
body.content .right-position .is-list {
    padding: 5px 0 10px 5px;
}

/* "Ei vielä kommentteja...", "Kirjaudu sisään..." text */
div.is-comments-widget p.no-messages, div.is-comments-widget div.not-logged-in-header, div.is-comments-widget div.not-logged-in-text {
    color: white;
}

/* Tilaajille -artikkelin otsikko, ingressi, kirjoittajan nimi */
.article-xl.single-article.xl-semifull .article-title, .article-xl.single-article.xl-semifull .article-ingress, .article-xl.single-article.xl-semifull .author a {
    color: white;
}

/* Etusivu, kaikki, jne */
.is-list a {
    color: white !important;
}
/* Comics title color */
span.hscomicnav-item-title {
    color: black;
}

/* "Haluatko lukea artikkelin" jne. */
.article-paywall .hardwall-text, .article-paywall .actions .action.login {
    color: white;
}

/* Some buttons */
div.is-comments-widget div.buttons > a, div.is-comments-widget div.buttons button,
article.single-article .subscribed.hs-follow-tag,
.is-list .button,
.is-more-items > .is-more-items-link {
    background-color: #4196A4;
}
div.is-comments-widget div.buttons > a.show-more-comments {
    color: white;
}
article.single-article .hs-follow-tag {
    border: 1px solid #4196A4;
}

/* Related articles background */
/* "Luitko jo nämä" */
.article-bottom .is-list .vignette,
.teaser-links-articlepack, .is-list span.vignette,
.article-middle .is-list .vignette {
    background-color: #382e44;
    padding: 2px 5px;
}

/* Article, comments, footer background */
.content-wrapper.article-body-container, article.single-article, .teaser-links, .content-wrapper.article-middle, .is-list.six-most-popular .most-popular, .article-middle .is-list, .content-wrapper.article-bottom, div.is-comments-widget > ul.comments .comment-content, .hs-header.sitewide, .main-header .header-inner-wrapper, header.main-header.sitewide, div.is-comments-widget > ul.comments li, div.is-comments-widget blockquote, .main-header, .main-footer,
.bg-hs-brand, .bg-hs-white {
	background-color: #211a24;
}

#mobile-navigation .level-one {
    background-color: #382e44;    
}
#mobile-navigation .level-one > li {
    border-top: 1px solid #4196A4
}

/* Link to article bottom color & width */
article.single-article .body a {
    border-bottom: 2px solid white;  
}

/* Dropdown menu "Uutiset" background, fixed header */
.section-or-tag-header {
    border: none;
    height: auto;
}
.section-or-tag-header h2, .secondary-header {
    background-color: #1a0b22;
}

.hs-ticker-hstv, .hs-ticker-diamond, .bg-hs-feature {
    background-color: #1b3134;
}

/* Luetuimmat bottom */
.is-list .is-most-read-articles-list .list-item:nth-child(1),  .most-read .list-item, .article-bottom .is-list {
    background-color: #2b2031;
}
.bg-hs-frost {
    background-color: #2b2031;
}
.bg-hs-frost .text-hs-coal {
    color: #e3e3e3;
}

/* Quoutations background */
article.single-article .body blockquote.ext-quote {
    background-color: #333333; 
}

/* Article bottom border */
article.single-article .related-links {
	border-bottom: 10px solid #1C1B1B;
    
}

/* Tilaa uutiskirje text */
.is-list .list-item .listitem-category,
.list-title,
#description {
    color: #4196A4;
}

.infobox {
    color: #e3e3e3;
	background-color: #211a24;
}

/* Fonts */
.text-hs-black,
article.single-article figcaption, .full-width-image-container figcaption,
.credit,
.teaser h2, h3, .articlepack-item .articlepack-link, .articlepack-title, .is-list .vignette, .is-list .tabs li a, .is-list .list-item > a p, .is-list .content, .supersaa-widget.theme-hs .supers-header .supersaa-title, .supers-header-row .city, .supers-header-row .time, .temperature, .wind-speed, .wind-speed-value, .live-schedule-item-time, .live-schedule-item-name, .live-schedule-item-description, .hsnl-title, .hsnl-item-title, article.single-article .article-metasection .author a, article.single-article .body .votsikko, article.single-article .body a.person, article.single-article .body, article.single-article .body a, h1.article-title, article.single-article .taglist .hs-subjects-header, article.single-article .taglist .tag, article.single-article .taglist .hs-tag-link, article.single-article .hs-follow-tag, .is-list.six-most-popular h3, span, div.is-comments-widget .commenting-header-info h2, div.is-comments-widget span.creator.nickname /*div.is-comments-widget .title span.comment-count*/, .comment-content, div.is-comments-widget header.comments-header, div.is-comments-widget div.buttons > a.show-more-comments-not-logged-in, #mobile-navigation .level-one > li.inline-mobile-nav a, #mobile-navigation .level-one .has-subs a, .fact-box-body, .fact-box-title, div.is-comments-widget blockquote, .main-footer .footer-link, article.single-article .body h2.mid-weight, .hsnl-grid-3col .hsnl-grid-item, a, .article-xl.single-article.xl-picture-top .xl-header-picture-top .article-title, .article-xl.single-article.xl-picture-top .xl-header-picture-top .article-ingress, .article-xl.single-article.xl-picture-top .author a,  article.single-article .body ul.sndp-ul, article.single-article .body ol.sndp-ol, article.single-article .person-meta ul.sndp-ul, article.single-article .person-meta ol.sndp-ol, .live-post .livepost-title, .live-post-byline time, .paywall-content {
    color: #e3e3e3;
}

.paywall-content p a, .text-hs-news, .text-hs-feature, .text-hs-local, .text-hs-cherry {
    color: #4196A4;
}

/* Comment sorter */
div.is-comments-widget header.comments-header .select-container .select select {
    color: white;
    border-color: white;
}

.scrolling-text span {
    background-color: #333333; 
}

#oikotie_tyopaikat_box_iframe {
    display: none;
}

.bg-hs-snow {
    background-color: hsl(163.1, 45.8%, 30.4%)
}
.main-footer .footer-row {
    max-width: none;
}
.main-footer .footer-logo {
    background-color: antiquewhite;
    width: 100%;
}

}
