css:
@-moz-document url-prefix("https://pixees.fr/informatiquelycee/") {
/********************************
	Made by EvanG and MazeWave 
 ********************************/
.act p:last-child {
    position: relative;
}
.act p:last-child::after {
    position: absolute;
    right: 0;

    content: 'Theme by EvanG and MazeWave';
    color: grey;
}

body {
    background-color: rgb(24, 24, 24);
    color: #95c0ea;
}

.act p {
    color: white;
}
.act h4,
.act h5 {
    color: #999;
}
.act ul li {
    color: #faa;
}
.act img {
    max-width: 100%;
}
.act img[alt="algo"] {
    filter: invert(1);
}
.act img:not([alt="algo"]) {
    background: white;
    padding: 1em;
}
.act img[src="img/cc.png"] {
    padding: 0;
}
.act table {
    color: white;
}
.act td,
.act th {
    border: 2px solid white;
    padding: 0.5em;
}
.act th {
    background: #00000080;
    font-size: 1em;
}
.act td {
    font-size: 0.75em;
}
ol {
    color: #bbb !important;
}
iframe {
    width: 100%;
    height: calc(100vw / 4);
}

pre {
    border: none;
    padding: 0;
    background: none;
}
pre code {
    background: #1d1f21;
    color: #c5c8c6;
    display: block;
    padding: 0 2.5em !important;
}
.tile {
    background-color: transparent;
    border: 5px solid rgb(34, 34, 34);
}

.navbar-default {
    background-color: #222;
}
.navbar-default .navbar-brand {
    color: white;
}

img[src="n_site/img/mail.png"],
img[src="n_site/img/twitter.png"] {
    filter: invert(1);
}
img[src="n_site/img/github.png"] {
    filter: invert(0.9);
}

/* Ancien site */
.navbar-default .navbar-nav > li > a {
    color: #7ae !important;
}
.navbar-default .navbar-nav > .dropdown > a .caret {
    border-color: #7ae transparent !important;
}
.dropdown-menu,
.select2-drop {
    background-color: #222;
}
.dropdown-menu > li > a {
    color: #bbb;
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus {

    color: #bdd;

    background-color: rgba(0, 0, 0, 0.5);
}
.anecdote {
    background: #111;
    border: none;
}
}
