css:
@-moz-document domain("antyradio.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #fed288 #1c1c1c;
}
a,h1,h2,h3{
    color: #d71921 !important;
}
p{
    color: #fff;
}
/*HEADER*/
header#header-main section.header.bottom{
    background: #1c1c1c;
}
.smallWeather .head .select-option ul li span{
    color: #fed288;
}
.smallWeather .head .select-option ul li span:hover{
    color: #d71921;
}
.iconWeather i{
    color: #fff;
}
nav#mainNav > :hover{
    color: #fed288 !important;
}
/*wYSZUKAJ*/
header#header-main section.header.search{
    background-color: #fed288;
}
form#header-search input{
    color: #d71921;
}
form#header-search input::placeholder{
    color: #000;
}
/*WYSZUKAJ*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
main#wrapper > .container{
    background: #1c1c1c;
}
.tiled6.esi-tiled aside .list-else > * a date{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.social.left-side-article .social-container,.jsmodule-voicer .voicer-button[data-template="circle"].up .inset,.jsmodule-voicer .voicer-button[data-template="circle"].down .inset{
    background: #1c1c1c;
}
.social.left-side-article .facebook-counter,.jsmodule-voicer .voicer-button[data-template="circle"][data-button-size="50"].voted .label,.voicer-content.footer h5{
    color: #fff;
}
.core-articles ul li .text span.title a{
    color: #d71921 !important;
}
.embed.article, .embed.felieton,blockquote{
    background-color: #fed288;
}
blockquote p{
    color: #1c1c1c;
}
/*ANKIETA*/
.content-view-embed.poll .info .label,.content-view-embed.poll .info .value{
    color: #fff;
}
.content-view-embed.poll .pollResults .answer .barContent{
    background: #1c1c1c;
    border: 1px solid #fed288;
}
/*ANKIETA*/
/*ARTYKUŁ*/
/*PODSTRONY*/
/*MUZYKA*/
.schedule-mini .item{
    background: #1c1c1c;
}
.schedule-mini .arrow{
    background: #fed288;
}
.schedule-mini .arrow:hover{
    background: #d71921;
}
.schedule-mini .item.active .text h3,.schedule-mini .item .time{
    color: #fed288 !important;
}
/*MUZYKA*/
/*PODSTRONY*/
/*FOOTER*/
footer#footer-main .copyright:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #fed288;
}
/*FOOTER*/
}

@-moz-document domain("sklep.antyradio.pl") {
html,body{
    background: #1c1c1c;
}
/*HEADER*/
.header-main .nav > li > a:hover{
    color: #fed288 !important;
}
.ubermenu .ubermenu-target-text{
    color: #fed288;
}
.ubermenu.ubermenu-main .ubermenu-item-level-0:hover > .ubermenu-target, .ubermenu-main .ubermenu-item-level-0.ubermenu-active > .ubermenu-target,.ubermenu-main .ubermenu-submenu.ubermenu-submenu-drop{
    background: #1c1c1c;
}
.nav-dark .form-flat input:not([type="submit"]){
    background: #1c1c1c !important;
}
.searchform-wrapper.form-flat .flex-col:last-of-type{
    margin-left: -3.9em;
}
.searchform .button.icon button i,.searchform .button.icon.button i{
    top: -2.5px;
    right: -18px;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
#wrapper, #main,.bg-loaded{
    background: #1c1c1c;
}
.slider-nav-reveal .flickity-prev-next-button.previous,.slider-nav-reveal .flickity-prev-next-button.next{
    background: #d71921;
    opacity: 1;
}
.slider-nav-reveal .flickity-prev-next-button.previous:hover,.slider-nav-reveal .flickity-prev-next-button.next:hover{
    opacity: 0.6;
}
.product.product-small .box-text a h5{
    color: #d71921
}
.product.product-small .box-text h5{
    color: #fed288;
}
.product.product-small span.amount{
    color: #fed288;
}
/*STRONA-GŁÓWNA*/
/*PRZEDMIOT*/
input[type="search"],input[type="search"]:focus,.page-title-inner select{
    background-color: #1c1c1c;
    color: #fff;
    box-shadow: none;
}
.is-divider{
    background: #d71921;
}
.widget .wc-layered-nav-term [data-route]{
    color: #d71921;
}
/*PRZEDMIOT*/
/*FOOTER*/
.footer-1{
    background: #1c1c1c;
}
.footer-1 p{
    color: #fff !important;
}
.copyright-footer{
    color: #fff;
}
.copyright-footer:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #fed288;
}
/*FOOTER*/
}
