@-moz-document domain("lyricstranslate.com") {
/* Invert everything by 90% */
html {
    color: #ffffff;
    background: #000000;
    filter: invert(90%) !important;
}

/* Inverting images or video does not do any good - invert them back */
img,
video {
    filter: invert(100%) !important;
}

.youtube-player div img,
.youtube-player > iframe:not(:fullscreen),
div.video-filter,
div.embedded-video {
    filter: invert(100%);
}

/* Revert Google and Facebook icons */
.hybridauth-google-hybridauth-32,
.hybridauth-facebook-hybridauth-32 {
    filter: invert(100%) !important;
}

/* Change color of text selection */
::selection {
    background-color: #a4a4a4;
}

/* Make the highlighting more visible when hovering over lyrics in a translation, or on the highlighted translation requests */
div[style="background-color: cornsilk;"],
.ltsearch-results-line .familiar td {
    background-color: #ddd9c9 !important;
}

/* Make links not look ugly */
a:link {
    color: #654800;
}

a.frontblocklink {
    color: #654800 !important;
}

a:visited {
    color: #1c3800;
}

/* Other general color corrections in some buttons & stuff */
#nav li ul a span,
ul.primary li a,
.ctype-menu ul li a,
span.blockrighttab-a,
div.more a,
div.more-link a,
.forum-post-footer .forum-post-links a,
.forum-post-links-top ul li a {
    color: #654800;
}

.node-links ul.node-links-right li a,
.nodelikeforum.nodelikelink a.nodelike,
.nodelikeforum.nodelikelink a.nodeunlike,
.nodelikecollection.nodelikelink a.nodelike,
.nodelikecollection.nodelikelink a.nodeunlike,
.nodelikeltphrasestranslation.nodelikelink a.nodelike,
.nodelikeltphrasestranslation.nodelikelink a.nodeunlike,
.commentlikelink a.commentlike,
.commentlikelink a.commentunlike,
.blockcommentlikes a.commentlike,
.blockcommentlikes a.commentunlike,
.nodelikeforum.nodelikelink a.nodelikecount,
.nodelikecollection.nodelikelink a.nodelikecount,
.nodelikeltphrasestranslation.nodelikelink a.nodelikecount,
.commentlikelink a.commentlikecount {
    color: #654800 !important;
}

a.ltusertranslationscontributionopen,
div#ltratestarsval a.votebyniddetails,
table.thankstable a.ltnodethanks{
    border-bottom: 1px dashed #654800;
}

/* Fix the colors of some menus */
.select2-container--default .select2-results__option--highlighted[data-selected] {
    background-color: #a76804;
}
}
