css:
@-moz-document domain("echo.msk.ru") {
html {
    color: #aaa;
    background: #2B2B2B
}

body {
    background: #2B2B2B;
}
.header_body {
    background: #1B1B1B;
	border-bottom: 1.25em solid #1B1B1B;
}
.tags {
    background: #4b4b4b !important;
}
.discuss {
    background: #1B1B1B;
}
.askthelive {
    background: #1B1B1B;
}
.typical {
    color: #ddd;
}
.conthead.blog .date {
    color: #aaa;
}
a, a:link {
    color: #eee;
}
a:hover {
    color: #ed1c24;
}
a.dark:hover {
    color: #ed1c24;
}
.lite {
    color:#959595 !important;
}
.black {
    color:     #b50a0a !important;
}
.carouselnav li {
    background-color: #171717;
	border: solid 1px    #4d4d4d;
}
.carouselnav .num {
    color: #e6e6e6    
}
.carouselnav li.current, .carouselnav li.current .num {
    color: #ed1c24;
}
.preview .prevcontent {
    background:     #393939;
}
.secondsubmenu {
    background:     #222;
}
.secondmenusection:hover a.menulink {
    background:     #222;
}
.carouselnav li{
    background-color: transparent;
    border: solid 1px #4d4d4d;
}
.carouselnav .wsico {
    color: #707070;
}
.inpselblock {
    background: none;
    color: #eee;
}
.commBlock {
    color: #eee !important;
}
.hotlinks h2 {
    background: #2B2B2B;
}
.preview.hover .prevcontent {
    box-shadow: 0 0 0 0.25em #1B1B1B;
}
.preview .mediamenu {
    border: none;
    
}
.preview .mediamenu a {
    background: #2B2B2B;
    color: #fff;
    border-left: none;
}
.dark {
    color:     #88868c !important;
}
.preview .title {
    color:    #b9b9b9;
}
.preview .mediamenu a .text {
    color: #fff;
}
.preview .mediamenu a .size {
    color: #888;
}
.conthead .person .name {
    color: #ececec;
}
.multimedia .mediamenu a {
    background: #2f2f2f;
    color:    #b9b9b9
}
.multimedia .mediamenu a .text {
    color: #fff;
}
.multimedia .mediamenu a .size {
    color: #888;
}
.aboutbroadcast {
    background: #3e3e3e;
}
.aboutbroadcast .text {
    color: inherit;
}
}
