css:
@-moz-document domain("wiez.com.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #fff #1c1c1c;
}
p,a{
    color: #fff !important;
}
a:hover{
    color: #d90000 !important;
}
/*HEADER*/
#fusion-gmap-container, #main, #sliders-container, #wrapper, .fusion-separator .icon-wrapper, body, html,.fusion-secondary-header,#side-header, .fusion-header, .layout-boxed-mode .side-header-wrapper, .side-header-background,.fusion-secondary-main-menu,.fusion-header-wrapper.fusion-is-sticky .fusion-header, .fusion-header-wrapper.fusion-is-sticky .fusion-secondary-main-menu{
    background: #1c1c1c;
}
.fusion-is-sticky .fusion-sticky-menu-only.fusion-header-v1 .fusion-secondary-main-menu, .fusion-is-sticky .fusion-sticky-menu-only.fusion-header-v2 .fusion-secondary-main-menu, .fusion-is-sticky .fusion-sticky-menu-only.fusion-header-v3 .fusion-secondary-main-menu, .fusion-is-sticky .fusion-sticky-menu-only.fusion-header-v4 .fusion-secondary-main-menu, .fusion-is-sticky .fusion-sticky-menu-only.fusion-header-v5 .fusion-secondary-main-menu{
    box-shadow: none;
}
.fusion-logo-link img{
    filter: invert(100%);
}
.fusion-secondary-menu a span,.fusion-main-menu > ul > li > a,.fusion-main-menu .fusion-main-menu-icon::after{
    color: #fff;
}
.fusion-main-menu{
    border-top-color: #fff;
}
/*WYSZUKAJ*/
.fusion-main-menu .fusion-main-menu-search .fusion-custom-menu-item-contents{
    background: #1c1c1c;
}
#wrapper .search-table .search-field input{
    background: #1c1c1c;
    color: #fff;
}
.searchform .search-table .search-button input[type="submit"]{
    background: #d90000;
}
/*WYSZUKAJ*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
.fusion-post-content h1, .post-content h1, .title h1,.wiez-books-title,.wiez-books-price-old{
    color: #fff;
}
.fusion-recent-posts.layout-columns-4 .entry-title a:hover{
    color: #d90000;
}
.home #fm_form_1,.recommendation-box{
    background: #1c1c1c !important;
}
.home #fm_form_1 input{
    background: #1c1c1c;
    color: #fff !important;
    border-color: #d90000 !important;
}
.title-sep.sep-single{
    border-color: #fff !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
#main .post h2,.post-content h3,.single-post .about-author-container{
    color: #fff;
}
.patronite_cta p{
    background: #1c1c1c;
}
/*KOMENSTARZE*/
.single-post .fusion-title h3{
    color: #fff;
}
#comment-input input,#comment-textarea textarea{
    background: #1c1c1c;
    color: #fff;
    border-color: #d90000;
}
/*KOMENSTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
#fm_form_3{
    background: #1c1c1c !important;
}
#sidebar .sub_header,#sidebar .sub_header strong{
    color: #fff;
}
input[type="text"]{
    background: #1c1c1c;
    color: #fff !important;
    border-color: #d90000 !important;
}
/*WYSZUKAJ*/
#main h2{
    color: #fff;
}
/*WYSZUKAJ*/
/*PODSTRONY*/
/*FOOTER*/
.fusion-footer-widget-area{
    border-top: 0;
}
#menu-widget-2 ul li a:hover{
    color: #d90000 !important;
}
.footer-copyright:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #d90000;
}
/*FOOTER*/
}

@-moz-document domain("wiez.pl") {
html,body{
    background: #000;
}
.HYPE_scene{
    background: #000 !important;
}
.HYPE_element{
    filter: invert(100%);
}
}
