css:
@-moz-document domain(emacswiki.org) {
html > * {
  background: #282a36 !important;
  color: #f8f8f2 !important;
  border-color: #44475a !important;
}

a {
  background: #282a36 !important;
  color: #ffffff !important;
  font-weight: 600;
  text-decoration: underline;
}
a:visited {
  background: #282a36 !important;
  color: #aaaacc !important;
}

.btn {
  color: #f8f8f2 !important;
  background-color: #6272a4;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #6272a4), color-stop(100%, #6272a4));
  background-image: -webkit-linear-gradient(#6272a4, #6272a4);
  background-image: linear-gradient(#6272a4, #6272a4);
  border: 1px solid #44475a !important;
}
.btn:visited {
  color: #f8f8f2 !important;
  background-color: #6272a4;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #6272a4), color-stop(100%, #6272a4));
  background-image: -webkit-linear-gradient(#6272a4, #6272a4);
  background-image: linear-gradient(#6272a4, #6272a4);
  border: 1px solid #44475a !important;
}
.btn:hover {
  background-color: #6272a4;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #6272a4), color-stop(100%, #6272a4));
  background-image: -webkit-linear-gradient(#6272a4, #6272a4);
  background-image: linear-gradient(#6272a4, #6272a4);
  -webkit-transition: none !important;
  -moz-transition: none !important;
  transition: none !important;
  background-position: 0 !important;
}

h1,
h2,
h3 {
  text-shadow: none !important;
}

.table {
  background-color: #282a36 !important;
  border: 1px solid #44475a !important;
}

body input,
body textarea,
body select,
body button {
  background-color: #4f4f4f !important;
  color: #f8f8f2 !important;
  border: 1px solid #44475a !important;
}

code,
pre {
  font-family: "Source Code Pro", "Menlo", "Monaco", monospace;
  font-size: 80%;
  background-color: #21222d !important;
  color: #f8f8f2 !important;
  border: 1px solid #44475a !important;
}

.navbar-inner {
  background-color: #282a36;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #282a36), color-stop(100%, #3f3f3f));
  background-image: -webkit-linear-gradient(#282a36, #3f3f3f);
  background-image: linear-gradient(#282a36, #3f3f3f);
  border: 1px solid #44475a !important;
}

.navbar .nav > li > a {
  text-shadow: none !important;
}
.navbar .nav > .active > a {
  color: #dfaf8f !important;
  background-color: #383838 !important;
}
.navbar .nav > .active > a:hover,
.navbar .nav > .active > a:focus {
  color: #dfaf8f !important;
  background-color: #383838 !important;
}

.toc {
  background-color: #282a36 !important;
  border: 1px solid #44475a !important;
}

div.two,
div.one {
  color: #f8f8f2 !important;
  background-color: #6272a4 !important;
  box-shadow: none !important;
}

.footer_wrapper {
  background-color: #3f3f3f !important;
  background-color: #3f3f3f;
  background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #3f3f3f), color-stop(100%, #4f4f4f));
  background-image: -webkit-linear-gradient(#3f3f3f, #4f4f4f);
  background-image: linear-gradient(#3f3f3f, #4f4f4f);
}

span.builtin {
  color: #50fa7b !important;
}
span.comment {
  color: #6272a4 !important;
}
span.constant {
  color: #ff79c6 !important;
}
span.function {
  color: #bd93f9 !important;
}
span.keyword {
  color: #ff79c6 !important;
  font-weight: bold !important;
}
span.string {
  color: #f1fa8c !important;
}
span.type {
  color: #bd93f9 !important;
}
span.warning {
  color: #ff5555 !important;
  font-weight: bold !important;
}
span.comment span,
span.string span {
  color: inherit !important;
}
span.comment span.important.constant,
span.string span.important.constant {
  color: #6272a4 !important;
}
span.linecomment {
  color: #6272a4 !important;
}
span.quote {
  color: #ffb86c !important;
}
}
