css:
@-moz-document url-prefix("https://www.nporadio2.nl/online-radio-luisteren/"), url-prefix("https://www.nporadio2.nl/soulenjazz/online-radio-luisteren/"){
/**
 * created 20191213
 */

/* DEFINE VARIABLES */
:root {
    --lighterBG: #444;
    --darkBG: #2d2d2d;
    --text: #fff;
}
/* CENTER STRUCTURE AND HIDE AD BOX */
.advertisement-wrapper {
    display: none;
}
.wrapper {
    width: 800px;
    margin-left: auto;
    margin-right: auto;
}
.nporadio2 .header {
    border-right: 0;
}
/* DARK THEME */
.nporadio2 {
    background-color: var(--lighterBG);
}
.grid {
    background-color: var(--darkBG);
}
/* HEADER */
.top {
	display: none;   
}
/* TAB: GEDRAAID */
.nporadio2 .playlist-item__time {
    color: var(--text);
}
.nporadio2 .playlist-item {
    background: var(--darkBG);
    color: var(--text);
}
/* TAB: GIDS */
.gids__day, .gids-item__content * {
    color: var(--text);
}
/* TAB: NIEUWS */
.nieuws-item__content {
    background-color: var(--darkBG);
    color: var(--text);
}
/* TAB: FREQUENTIES */
.frequencies-wrapper, .frequencies__dab__title, .frequencies__download__title, .frequencies__download__body ul li a {
    color: var(--text) !important;
}
.frequency__row {
    background-color: var(--lighterBG) !important;
}
}
