/* ==UserStyle==
@name           NPO Radio 2 / NPO 3FM Radio Player Dark Mode & Minimalistic (by golabs, October 2021)
@namespace      stylus
@version        0.9.6
@homepageURL    https://golabs.nl/stylus/    
@updateURL      https://golabs.nl/stylus/usercss/radio_npo2_npo3.user.css
@license        CC-BY-NC-SA-4.0
@description    Minimalistic Dark Mode for NPO Radio 2 / NPO 3FM Radio Player
@author         golabs
==/UserStyle== */
@-moz-document
url-prefix("https://www.nporadio2.nl/online-radio-luisteren/"),
url-prefix("https://www.nporadio2.nl/soulenjazz/online-radio-luisteren/"),
url-prefix("https://www.npo3fm.nl/online-radio-luisteren/"),
url-prefix("https://www.npo3fm.nl/kx/online-radio-luisteren/"),
url-prefix("https://www.npo3fm.nl/alternative/online-radio-luisteren/")
{ 
      /* DEFINE VARIABLES */
      :root {
          --lighterBG: #444;
          --darkBG: #2d2d2d;
          --text: #fff;
      }
      /* CENTER STRUCTURE AND HIDE AD BOX */
      .advertisement-wrapper {
          display: none;
      }
      .wrapper {
          width: 800px;
          margin-left: auto;
          margin-right: auto;
      }
      .nporadio2 .header {
          border-right: 0;
      }
      /* DARK THEME */
      body, .nporadio2 {
          background-color: var(--lighterBG);
      }
      .grid {
          background-color: var(--darkBG);
      }
      /* HEADER */
      .top {
      	display: none;   
      }
      /* TAB: GEDRAAID */
      .nporadio2 .playlist-item__time,
      .npo3fm .playlist-item__time {
          color: var(--text);
      }
      .nporadio2 .playlist-item,
      .npo3fm .playlist-item {
          background: var(--darkBG);
          color: var(--text);
      }
      /* TAB: GIDS */
      .gids__day, .gids-item__content * {
          color: var(--text);
      }
      /* TAB: NIEUWS */
      .nieuws-item__content {
          background-color: var(--darkBG);
          color: var(--text);
      }
      /* TAB: FREQUENTIES */
      .frequencies-wrapper, .frequencies__dab__title, .frequencies__download__title, .frequencies__download__body ul li a {
          color: var(--text) !important;
      }
      .frequency__row {
          background-color: var(--lighterBG) !important;
          color: var(--text) !important;
      }
      .npo3fm .dab__map_container {
      	display: none;   
      }
}
