    border-top: 1px solid #dbdbdb;
    color: gray;
    text-align: right;
    font-size: smaller;
    padding-top: 0px;
}

.dateline a:link, .dateline a:visited {
    background-color: #333333;
    color: white;
    padding: 0 .3em 0 .3em;
}

