css:
@-moz-document url-prefix("https://www.watch2gether.com/rooms/") {
.w2g-topbar {
    display: none;
    pointer-events:none;
}
.w2g-search-results {
    display: none;
    pointer-events:none;
}
.w2g-disabled {
    display: none;
    pointer-events:none;
}
.w2g-main-container.w2g-sidebar-open .w2g-sidebar {
    display: none;
}
.w2g-userbar {
    position: fixed;
}
.w2g-color-wrapper {
    min-height: 0;
}
.w2g-scroll-vertical {
    overflow-x: unset!important;
    overflow-y: unset!important;
    visibility: hidden;
}
.w2g-userbar {
    position: fixed;
    background-color: #000000a1!important;
}
.w2g-player-search {
    min-width: 728px;
    max-height: 100%;
    flex-grow: 1;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    position: relative;
    top: 40%;
    transform: scaleY(1.558);
}
#mirror_video_ele {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 100;
    object-position: center;
    object-fit: fill;
}
.ui.tiny.menu {
    font-size: .85714286rem;
    transform: scaleY(0.55);
    position: relative;
    margin-top: -6.919px;
    opacity:0.3;
}
.ui.tiny.menu:hover {
opacity:1;
}
i.angle.double.right.icon {
    display: none;
}
.w2g-main-right {
    position: relative;
    width: 24vw;
    max-width: unset!important;
    max-height: 100%;
    flex-shrink: 0;
    min-width: unset!important;
    display: flex;
    flex-direction: column;
    margin-left: -4px;
}
.w2g-content {
    display: flex;
    overflow: hidden;
    flex-grow: 1;
    background-color: black;
}
.w2g-player-menu {
    width: 100%;
    position: relative;
    flex-shrink: 0;
    background-color: black;
}
element.style {
    background-color: rgb(0, 0, 0)!important;
}
}
