css:
@-moz-document url-prefix(http://shieldvision),
               url-prefix(https://shieldvision),
               domain(shieldvision-next.soc.datashieldprotect.com),
               domain(shieldvision-next2.soc.datashieldprotect.com)
{
/* background */
    body{
        background-color: #222;
    	color: red;   
    }

    .main-content{
        color: white;
    }

    .main-content h1{
        color: white;
    }

    /* side panel menu items */
    .fuZLQh, .sc-fMiknA, .loZxnQ{
    	color: #ccc;
        /*background-color: red;*/
    }

    .fuZLQh:hover, .sc-fMiknA:hover, .loZxnQ:hover{
    	color: white;
        background-color: rgba(0, 0, 0, 0.25);
    }

    /* sidebar */
    .cTHevg, .sc-iELTvK, .fabNJZ, .sc-bMvGRv, .VebSZ, .sc-bRBYWo .icywuZ, .sc-fBuWsC, .frtayl{
        color: red;
        background-color: red;
    }

    /* page dropdown text */
    .kFKhaA{
        color: red;
    }

    input {
        background-color: #333;
    }

    /*
    .sc-iELTvK{
        color: white;
        background-color: #333;    
    }

    .fabNJZ{
        color: white;
        background-color: #333;
    }

    sc-bMvGRv

    VebSZ */

    /* sidebar items */

    /* current */
    .gwwqsk{
        background-color: red;
        color: white;    
    }

    /* base */
    .zvomO {
        background-color: red;
        color: white;
        /*border-right: 5px solid rgba(0, 0, 0, 0.2);*/
    }

    /* hover */
    .zvomO:hover {
        background-color: rgba(0, 0, 0, 0.5);
        color: white;
        /*border-right: 5px solid rgba(0, 0, 0, 0.2);*/
    }

    .datatable div{
    	color: white;   
    }

    /* unfuck ellipses and word wrapping in results */

    tr.has-subdata, tr.has-subdata:hover, tbody.body-row, tbody.body-row:hover{
        background-color: #666;
        /*color: red;*/
    }

    /* background of table and table header */
    .datatable-container {
    	white-space: normal;
        background-color: #333;
        /*confirmed header color - color: #ccc;*/
    }

    /* background of table rows */
    .datatable .body-row td {
    	white-space: normal;
        background-color: #333;
        border-bottom: 1px solid black;
        /*color: #ccc;*/
    }

    /* table row text color and wrapping */
    .datatable .body-row tr a div, .datatable .body-row tr div{
        color: #bbb;
        overflow: unset;
        text-overflow: unset;
        word-break: break-all; /* prevents overlap but breaks words */
    }

/* chart shit */
.white{
    background-color:#444 !important;
}

.card{
    background-color: #444;
}

.black-text{
    color: white !important;
}

/* profile link and version no */

.dropdown-toggle, .nav-link{
    background-color: #444;
    color: white;
}

.dropdown-toggle div, .nav-link div{
    background-color: #444;
    color: white;
}

/* sidebar background */
#root > div > div > div{
	color: white;
    background-color: purple;
}

/* sidebar items */
#root > div > div > div > div > div > a{
	color: white;
    /*background-color: blue; - not necessary */
    background-color: rgba(0, 0, 0, 0.1);
}

/* sidebar items */
#root > div > div > div > div > div > a :active{
	color: red;
    background-color: blue !important;
}

/* sidebar items */
#root > div > div > div > div > div > a :hover{
	color: red;
    /*background-color: blue; /*- unnecessary since none is clear and lets you see background color from main side panel*/
    background-color: rgba(0, 0, 0, 0.25);
}

/* sidebar items selected / active | doesn't work bc SVN doesn't mark the active item active.. they use arbitrary class shit
#root > div > div > div > div > div :active{
	color: white;
    background-color: black;
}*/

/* sidebar items selected / active */
#root > div > div > div > div > div :hover{
	color: white;
    background-color: rgba(0, 0, 0, 0.25);
    /*background-color: black;*/
}

/* sidebar | obscured by sidebar item bgcolors
#root > div > div > div > div {
	color: white;
    background-color: red;
}*/

/* version number  
#root > div > div{
	color: red;
}*/

/* page size (number of items) dropdown text */
.select-dropdown, .caret, div.svnc-select.undefined, div.svnc-select.undefined > div {
    color: white !important;
    border-top-color: white; /* caret for page size */
    border-bottom-color: white;
}
/* page size dropdown menu items | gets text color from main-content */
div.svnc-select-popup, .filtrable {
	color: blue;
	background-color: purple;
}

/* profile menu dropdown background and button text */
div.dropdown-menu {
	background-color: purple;
}

div.dropdown-menu > button {
	color: blue;
}

}
