css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://twitter.com/") {
div#header
{
position:fixed;
width:763px;
background-position:5px center !important;
z-index:2;
}
a#logo img
{
left:5px !important;
}
div.content-bubble-arrow
{
position:fixed;
top:-12px;
width:764px;
height:180px;
background-color:white;
z-index:1;
}
table.columns
{
position:relative;
top:41px;
}
div.wrapper
{
position:relative;
}
div#status_update_box,
div#dm_update_box
{
position:fixed;
width:543px;
background-color:white;
z-index:2;
}
td#content div.section
{
position:relative;
top:125px;
margin-bottom:125px !important;
}
div#side_ad_base
{
position:fixed;
width:198px;
padding-bottom:5px !important;
background-color:white;
z-index:3;
}
div#side
{
position:relative;
top:195px;
}
div#footer
{
margin-top:33px !important;
}
}
