css:
@-moz-document domain("mako.co.il") {
body, .ob-smartfeed-joker {
  background-color: #000;
  color: #EEE;
}

nav.responsive, .navWrap nav.responsive {
  background-color: #A9B6BA;
}

.mako_scroller_container,
.mako_scroller_container iframe,
.mako_scroller_container img,
.mako_scroller_container .html5_player:not(.full_screen),
.mako_scroller_container .newsBtn,
div.free-items ul li .image_inside a img, .hover .image_inside, .hover .image_inside a {
  filter: invert(100%);
  opacity: 0.85;
}

.article iframe,
.article twitter-widget,
.article img,
.article .html5_player:not(.full_screen) {
  opacity: 0.85;
}


.mako_scroller_container iframe:hover,
.mako_scroller_container img:hover,
div.free-items ul li .image_inside a img:hover, .hover .image_inside:hover, .hover .image_inside a:hover,
.article iframe:hover,
.article twitter-widget:hover,
.article img:hover {
  opacity: 1;
}

article .article-header header h1,
article .article-header header h2,
article .article-body h4,
header.skin-v_2019 strong a,
nav.breadcrumbs-v_2017 ul li,
body:not(.news) nav.breadcrumbs-v_2017 ul li a,
.empty-comments .befirst-paragraph,
.mako_comments__h3,
.feelingsWrapper .feelingItem,
#feelingsMako h3, #vote h3,
.helpful_footer_links h4,
#age-msg strong,
html .tab_title_text,
html .answer_container {
  color: #EEE;
}

.helpful_footer_links a,
.mklist__item__text {
  color: #DDD;
}

.mako_scroller_container,
.mako_scroller_container .helpful_footer_links h4 {
  color: #000;
}

.mako_scroller_container .helpful_footer_links a {
  color: #222;
}

.mako_form_comments input, .mako_form_comments .mako_form_comments__text,
.article .footer__showmore {
  opacity: 0.6;
}

header.skin-v_2019 {
  border-color: #EEE;
}

.highScorePage, .tab {
  background-color: #333;
  color: #EEE !important;
}

/* # Clear # */
body {
  zoom: 110%;
}

.main_page .picg + a, .main_page .picg + img,
.OUTBRAIN {
  display: none;
}

.main_page .mako_content_container .mako_main_portlet_group_container_td .mako_main_portlet_container {
  margin-bottom: 15px;
}

.tapet-wrap {
  filter: invert(100%);
}

aside.share-bar .fonts {
  filter: invert(100%);
  border-bottom-color: #333;
}

g#social-copy-2 {
  fill: #ddd;
}
}
