css:
@-moz-document url-prefix("https://cp.rpg-city.de/") {
body
{
  font-family:Arial;

  background: url(https://cp.rpg-city.de/images/bg_industrial.jpg) no-repeat center center fixed; 
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;

  padding: 60px 20px 0 20px;
  
  white-space: nowrap;
}

a
{
  text-decoration: none;
  color: #f7f7f7 !important;
  cursor: pointer;
}

#header
{
  width: 100%;
  height: 40px;

  font-size: 0.9em;
  line-height: 40px;

  padding-left: 15px;

  background-color: #171616;
}

#headerMenu
{
  text-transform: uppercase;
  text-shadow: 0 0 3px rgba(0, 0, 0, 0.6);
}

#navigationHeaderMenu a:hover
{
  text-decoration: underline;
}

.striped a:first-child
{
  padding-left: 0;
}
.striped a:last-child
{
  border: none;
  padding-right: 0;
}

#content
{
  top: 80px;
  left: 0;
  margin: 0 auto;
  
  min-width: 1000px;
  max-width: 1280px;

  height: 1px;
  min-height: 750px;
}

#contentHeader
{
  background-color: #171616;

  /*border-top-left-radius: 9px;*/
  /*border-top-right-radius: 9px;*/
}

#contentTitle
{
  margin-left: 20px;
  
  line-height: 70px;
  vertical-align: middle;
  
  font-size: 1.8em;
  color: #eee;
  text-transform: uppercase;
  display: inline-block;
}

#userInfo
{
  display: inline-block;
  
  float: right;
  margin-right: 15px;
  margin-top: 25px;
  
  font-size: 0.85em;
  color: #fff;
  text-align: right;
  text-transform: uppercase;
  text-shadow: 0 0 3px rgba(0, 0, 0, 0.6);
}

#userInfo i
{
  opacity: 0.4;
}

#userInfo a
{
  color: #fff;
}

#userInfo a:hover
{
  text-decoration: underline;
}

#userInfo img
{
  padding-left: 20px;
  vertical-align: middle;
  
  float: right;
}
 
#contentMain
{
  position: relative;
  top: -20px;
  width: 100%;
  
  min-height: 100%;
  
  background-color: #222222;

  /*
  border-bottom-left-radius: 9px;
  border-bottom-right-radius: 9px;
  */
}

#contentNavigation
{
  position: absolute;
  left: 0;
  top: -5px;
  width: 25%;

  min-height: 101%;
  
  background-color: #171616;
}

#contentNavigationMenu div
{
  width: 90%;
  height: 28px;
  
  margin: 5px 5%;
  text-align: left;
  
  line-height: 28px;
  vertical-align: middle;
  
  font-size: 0.85em;
  
  /*border: 1px #ccc solid;*/
  /*border-radius: 3px;*/
  background-color: #222222;
  /*box-shadow: 0 1px 0 rgba(170, 170, 170, 0.6);*/
}

#contentNavigationMenu div i
{
  margin-left: 10px;
}

#contentNavigation h1
{
  padding-left: 10px;
  margin-top: 20px;
  
  color: #4a4a4a;
  font-size: 1.25em;
}

#contentNavigation hr
{
  position: absolute;
  left: 10px;
  right: 10px;
}
  
.contentNavigationSidebox
{
  padding: 0 10px;
  
  font-size: 0.85em;
  color: #f7f7f7;
  
  white-space: normal;
}

/*
#contentNavigation ul
{
  padding: 1px 5px;
  list-style-type: none;
  
  margin-left: 3%;
  
  font-size: 0.85em;
}

#contentNavigation li
{
  margin: 0 0 10px 0;
  padding: 4px 4px 4px 8px;
  width: 90%;
  border: 1px #ccc solid;
  
  border-radius: 3px;
}
*/
#contentPage
{
  position: relative;
  left: 25%;
  width: 75%;
}

#contentPageTitle
{
  font-size: 1.5em;
  font-weight: bold;
  padding-top: 12px;
  margin-left: 15px;
  margin-bottom: 15px;
  color: #fff;
}

hr
{
  height: 1px;
  position: absolute;
  margin-top: -10px;
  left: 15px;
  right: 15px;
  background-color: #000;
  border: 0;
  opacity: 0.2;
}

#contentPageContent
{
  background-color: #222222;
  white-space: normal;
  overflow: hidden;
  padding: 15px 15px 15px 15px;
  margin-bottom: 30px;
  
  
  font-size: 0.85em;
  color: white;
}

#contentFooter
{
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 30px;
  margin-bottom: -30px;
  
  vertical-align: middle;
  line-height: 30px;
  
  background-color: #171616;
  color: #fff;
  font-size: 0.75em;
  text-align: center;
  text-transform: uppercase;
  text-shadow: 0 0 3px rgba(0, 0, 0, 0.6);

  /*border-bottom-left-radius: 9px;*/
  /*border-bottom-right-radius: 9px;*/
}

#contentFooter a:hover
{
  text-decoration: underline;
}

#contentFooterMenu
{
  position: absolute;
  left: 15px;
  padding-bottom: 40px;
}

#currentPlayers
{
  position: absolute;
  right: 15px;
}

#contentFooter i
{
  opacity: 0.4;
}

.striped a
{
  color: white;
  border-right: 1px rgba(255, 255, 255, 0.3) solid;
  padding-left: 15px;
  padding-right: 15px;
}

.current
{
  font-weight: bold;
}

.label-head {
  background-color: #ad0000;
  background-image: -webkit-linear-gradient(#ad0000,#8a261f);
  background-image: linear-gradient(#ad0000,#8a261f);
}

.label-admin {
  background-color: #0000db;
  background-image: -webkit-linear-gradient(#0000db,#0026cf);
  background-image: linear-gradient(#0000db,#0026cf);
}

.label-support {
  background-color: #009bee;
  background-image: -webkit-linear-gradient(#009bee,#00a2de);
  background-image: linear-gradient(#009bee,#00a2de);
}

.label-overwatch {
  background-color: #fa3;
  background-image: -webkit-linear-gradient(#fa3,#971);
  background-image: linear-gradient(#fa3,#971);
}


/*
GENERAL NEW BS COLORS:
info: #317793
primary: #3a7bb3
success: #418226
danger: #822626
warning: #b59b0d
*/

/* Overwrite BS CSS */
.table-hover > tbody > tr:hover > td, .table-hover > tbody > tr:hover > th,
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus
{
    background-color: #404040;
}

.pagination > li > a, .pagination > li > span,
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus
{
    background-color: #222;
}

.form-control {
    background-color: #4d4c4c;
    color: #fff;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: #4d4c4c;
    color: #fff;
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #9b9a9a;
}

.help-block {
    color: #aaa7a7;
}

.dropdown-menu,
.modal-content{
    background-color: #555353;
}

#carAuctions tr[id$='Details'] > td{
    background-color: #222 !important;
}

.progress {
    background-color: #848282;
}

.control-label,
.modal-title{
    color: #fff;
}


/* BTN OVERWRITE */
.btn-primary {
    background-color: #3a7bb3;
    border-color: #3a7bb3;
}

.spoiler,
.btn-success {
    background-color: #418226;
}

.btn-info {
    background-color: #317793;
}

.btn-danger {
    background-color: #822626;
}

/* BS Paneloverwrite */
.panel-info > .panel-heading{
    color: #fff;
    background-color: #29647b;
    border-color: #29647b;
}

.panel-primary > .panel-heading {
    color: #fff;
    background-color: #3a7bb3;
    border-color: #3a7bb3;
}

.panel-default > .panel-heading {
    color: #fff;
    background-color: #606060;
    border-color: #606060;
    
}

.panel-success > .panel-heading {
    color: #fff;
    background-color: #418226;
    border-color: #418226;
}

.panel-info,
.panel-primary,
.panel-default,
.panel-success{
    color: #fff;
    background-color: #506066;
}


/* BS TABLE */
.table .table {
    background-color: #555353;
}


.table > thead > tr > .success, .table > tbody > tr > .success, .table > tfoot > tr > .success, .table > thead > .success > td, .table > tbody > .success > td, .table > tfoot > .success > td, .table > thead > .success > th, .table > tbody > .success > th, .table > tfoot > .success > th {
    background-color:#418226;
}

.table-hover > tbody > tr > .success:hover, .table-hover > tbody > .success:hover > td, .table-hover > tbody > .success:hover > th{
    background-color: #56aa32;
}

.table > thead > tr > .danger, .table > tbody > tr > .danger, .table > tfoot > tr > .danger, .table > thead > .danger > td, .table > tbody > .danger > td, .table > tfoot > .danger > td, .table > thead > .danger > th, .table > tbody > .danger > th, .table > tfoot > .danger > th {
    background-color: #822626;
}

.table-hover > tbody > tr > .danger:hover, .table-hover > tbody > .danger:hover > td, .table-hover > tbody > .danger:hover > th {
    background-color: #aa3232;
}

/* BS Alerts */
.alert-warning,
.alert-success,
.alert-primary,
.alert-info,
.alert-danger {
    color: #fff
}

.alert-warning {
    background-color: #b59b0d;
}

.alert-success {
    background-color: #418226;
}

.alert-danger{
    background-color: #822626;
}

.alert-info {
    background-color: #317793;
}

/* BS LABELS */
.label-success {
    background-color: #418226;
}

.label-danger {
    background-color: #822626;;
}

.label-warning {
    background-color: #b59b0d;;
}
}
