css:
@-moz-document domain("nzbindex.com") {
html, body                                  { background-color: #181818; color: #e8e8e8 }

.table-hover tbody tr:hover>td,
.table-hover tbody tr:hover>th              { background-color: #202020 }

.table-striped tbody>tr:nth-child(odd)>td, 
.table-striped tbody>tr:nth-child(odd)>th   { background-color: #1c1c1c }

.table th, .table td                        { border-top: 1px solid #222 }

.pagination ul>li>a, 
.pagination ul>li>span                      { background-color: #282828; border: 1px solid #222 }

.pagination ul>li>a:hover, 
.pagination ul>li>a:focus, 
.pagination ul>.active>a, 
.pagination ul>.active>span                 { background-color: #333 }

.pagination ul>.disabled>span,
.pagination ul>.disabled>a, 
.pagination ul>.disabled>a:hover, 
.pagination ul>.disabled>a:focus            { background-color: #222 }

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"],
.uneditable-input, .well, #group-list       { background-color: #222; border: 1px solid #333; color: #e8e8e8 }

.alert                                      { text-shadow: none; background-color: #282828; border: 1px solid #222 }

/* Chrome scrollbar */
::-webkit-scrollbar {
    height: 19px;
    width: 19px;
    background: linear-gradient(0deg, #282828, #141414)
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(0deg, #09326a, #006dcc);
    border-radius:0.33ex;
    border: 1px solid #04c;
    transition: 0s all 1.3s ease;
}


#i { display: none !important }
}
