css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vkontakte.ru") {

body {background: url(http://fantaslib.narod.ru/themes/05.bmp) fixed !important;}

#content {
background: transparent no-repeat scroll left top !important;
}

#pageHeader {background: url(http://fantaslib.narod.ru/themes/BHead.bmp) top !important; position: fixed !important}

#sideBar { position: fixed !important; margin: 47px 3px 0px 7px !important; width:131px !important; }
#sideBar #nav { margin: 0px !important; }
#sideBar #nav a { overflow: hidden !important; white-space: nowrap !important; }

#pageBody { margin:43px 7px 5px 5px !important; width:638px !important; }

DIV, Select, TD, TEXTAREA, SPAN.ncc, Input, A, #quickquery, B, #endQuote, #startQuote, .actionspro, LI.written, TH, UL.pageList, .actionspro, ul.topNav li, #userProfile, #profileActions, .nc *, .groupslist table, #td.info {background: transparent none !important}

#rateLeft, .voting_result_fg {background: none !important}
#rateRight, .voting_result_bg {background: none !important}

a:hover, .header, #header, #content {background-image: #C3BAAF !important}

option, #boxWrap {background: #C3BAAF !important}

a, h1, h2 {color: #8D8D8D !important}
.bOpen, .flexHeader, .bShut, .flexHeader, #rateRightGold, .post, .postHeader, .wallpost, .info, .header{background: none !important;}

#myLink, td, small, caption, span, p, b, LI, Div, #nav li a, input, select, option, dd, label {color: #8D8D8D !important; }

.mailbox table tr.newRow {background: ghostwhite !important}

TEXTAREA { color: #8D8D8D !important;}

input {color: cadetblue !important;}

#nav li a:hover{ color: #B6B6B6 !important;}

#quickquery, div.actions, #myLink, #content, TextArea,Div.playline, TD, Input, select, TH, H4, H2, #photoactions a, #photoactions .action_link, #photoactions .non_action, .bNav li a, ul.buttonRow a, .header, .flexHeader, #wrapH
{border-color: #7B7363 !important}

#wrapH {border-bottom: 0px #A59D8E !important}

#sideBar #nav li {border: 0px #A59D8E !important}

#nav li a, .nc *, LI, p, B, #profileActions a, .pageList a, div
{border-color: transparent !important;}

.notes_titles li.app {background: transparent none !important}

#adContainer, #sideBar .ad_box, #sideBar .ad_box + div[style='margin-top: 3px; font-size: 10px; text-align: center;'], #sideBar>div>a[href='help.php?page=target'] { display: none !important; }

#banner3, #banner2, #banner1 {width:1px !important;height:1px !important;margin-left:-9999px !important}

}
