css:
@-moz-document domain("mon.lyceeconnecte.fr") {
.main, .notification, .low-importance.right-magnet, .actions i {
	color: white!important;
}
.edit {
	color: black!important;
}
.main, body, html {
    background: #333;
}

ul.icons-tabs li, .toggle-panel {
	background-color: #3498db;
}

em.skin-dyslexic, em.skin-default {
	background-color: #2980b9;
}

h1, h2, h3, h4, h5, a, .calendar.widget .current-month-header, .calendar.widget .content h2, .search-pagination input+i.search, .mailbox .notification .mail:before, nav.prev-next, .tabs.heading-tabs header a i, .underlined-title, .underlined-title strong, .notification div.notification-actions>.opener, .drop-down-block i.arrow, .info i.check-circle {
	color: #3498db!important;
}

.navbar, .progress-bar .filled, a.selected .badge, plus .opener, nav.prev-next button:enabled, .tabs header::after, .tabs header:hover::after, .calendar.widget .content .day.today {
	background: #3498db;
}
.lightbox>.background::before {
	background: #3498db;
}
popover popover-content {
	background: #333;
    color: white;
    border: 1px solid #444;
}

.search-text .searchInput {
	background: #444;
    color: white;
    border: none;
}

nav.horizontal i.mail span {
	background: #3498db;
    border: 1px solid #2980b9;
}

nav.horizontal>a:hover, nav.horizontal span>a:hover {
	border-color: white;
}

.drop-down-block .drop-down-label:hover, .drop-down-block .drop-up-label:hover {
	border-color: #3498db;
}

.notification article .avatar img, .notification article .avatar i.system-avatar {
	border: none;
}


side-panel.hidden .opener:after {
	border-color: transparent transparent transparent #3498db;
}
.notification, .notification .left-open, article.mail, article.write-mail {
	background: #444;
}

.notification .opener {
	border-left: none!important;
    border-right: none!important;
}

.actions, .widget-container, .widget-container:hover, .logbook-widget .ticket-card {
	background: #444;
}

.more.row {
	background: #555;
}

.more.row a {
	color: white;
}

.logbook-widget .logbook-sections {
	box-shadow: none;
}

.logbook-widget .ticket-card {
	color: white;
    border: none!important;
}
.logbook-widget .ticket-card section h5 span, .logbook-widget .ticket-card section p {
	color: white;
}
.logbook-widget .ticket-card:hover {
	background: #555;
}
section.lightbox>div.content, section.lightbox>.background+div.content>.twelve.cell>.twelve.cell, .logbook-widget section.lightbox article {
	background: #333;
    color: white;
    box-shadow: none;
}
.logbook-widget section.lightbox article>div {
	border: none;
}

.chip.selected.workspace, .sticker.app.workspace, .sniplets .sniplet.workspace {
	background-color: #3498db;
}

select {
	background: #444;
    border: none;
    color: white;
}
input[type=text], input[type=password], input[type=search], input[type=tel], input[type=email] {
	color: white;
}
input[type=text]:focus, input[type=password]:focus, input[type=search]:focus, input[type=tel]:focus, input[type=email]:focus, .zimbra .recipients .drop-down-label:hover hr, .info {
	border-color: #3498db;
}
recipient-list div.focus {
	border-color: #3498db;
    box-shadow: none;
}
.info {
	background: #444;
    color: white;
}
.share.temp .autocomplete>div {
	background: #555;
}
search-user.focus label {
	color: #3498db;
}

textarea {
	background: #555;
    color: white;
    border: 2px solid transparent;
}
textarea:focus {
	border-color: #3498db;
}
.message {
	background: #555;
    color: white!important;
    border-color: #3498db;
}

.progress-bar {
	border: none;
    background: #444;
}

.flat-button, button.flat-button, input[type=submit].flat-button, input[type=button].cancel, button.cancel, a.button.cancel, .drop-down-button label, .drop-down-button.hidden label, nav.vertical a.classic-link, .app-menu a.classic-link, button, input[type=button], a.button, input[type=submit], .drop-down-button label, .drop-down-button.hidden label {
	background: #3498db;
    color: white!important;
}

button.cancel:hover, input[type=button].cancel:hover, a.button.cancel:hover, input[type=submit].cancel:hover, nav.vertical a.classic-link:hover, .app-menu a.classic-link:hover, button:hover, input[type=button]:hover, a.button:hover, input[type=submit]:hover, plus:hover .opener {
	background: #2980b9
}

label.wrapping-checkbox input[type=checkbox]+span::before, label.wrapping-checkbox input[type=checkbox]+i18n::before {
	border-color: #3498db;
    background: #555;
}
.mailbox .notification.new-element {
	background: #555;
}

nav.vertical>ul, nav.vertical>div>ul, .app-menu>ul, .app-menu>div>ul, hr.line {
	border-color: #3498db;
}

.text-title, strong, .medium-importance {
	color: #ecf0f1;
}

div[bind-html="mail.body"] div, div[bind-html="mail.body"] ul li, div[bind-html="mail.body"] span {
	color: white
}

.loading-list ul li {
	background: #444;
    color: white;
}

.tabs.heading-tabs {
	background: #444;
}

article {
	background: #444;
}

.toggle-buttons {
	background: #3498db;
}

.toggle-buttons button:hover, .toggle-buttons .button:hover {
	background: #2980b9;
}

.icons-view .element a i, .icons-view .element a.container i, .icons-view .element .img.container i, .icons-view explorer a i, .icons-view explorer a.container i, .icons-view explorer .img.container i {
	color: #3498db;
}

explorer.explorer-hover i.folder-large:hover, explorer.explorer-hover i.unknown-large:hover, explorer.explorer-hover i.audio-large:hover {
	color:  #2980b9!important;
}

.icons-view .element a.container, .icons-view .element .img.container, .icons-view explorer a.container, .icons-view explorer .img.container {
	border-color: #333;
}

explorer.explorer-hover a.container:hover, explorer.explorer-hover legend:hover+a.container, .icons-view .element .selected a.container, .icons-view .element .selected .img.container, .icons-view explorer .selected a.container, .icons-view explorer .selected .img.container {
	border-color: #3498db!important;
}

.icons-view .element.selected legend {
	background: #333;
}
explorer.selected .container {
	background: #434343;
}
.media-library .import-files {
	background: #333;
    border-color: #444;
}
.media-library .import-files .cloud-upload, nav.vertical ul li a i.arrow::before, .app-menu ul li a i.arrow::before, .tipbox i {
	color: #3498db;
}
.embedded-viewer, .embedded-viewer .controls, table tr {
	background: #444;
}
.workspace-app table tbody tr:hover, .workspace-app ul.list-view tbody tr:hover {
	background: #555!important;
}
.workspace-app table tbody tr:hover *, .workspace-app ul.list-view tbody tr:hover * {
	color: inherit!important;
}
table thead tr, ul.list-view thead tr {
	background: #3498db;
}
.responsive-table {
	border: none;
}
.share.temp .autocomplete>div, .share.temp .autocomplete>div:hover a {
	color: white!important;
}

.share.temp .autocomplete>div:hover {
	background: #3498db;
}

.tooltip .content {
	background: #3498db;
}

.bookmarked-apps {
    border-color: #555;
}

.icons-view.apps .element:hover i::before, .icons-view.apps .element:hover a {
	color: #3498db!important;
}

.round.active {
	border-color: #3498db;
}

.underlined-title {
	border-color: #3498db;
}

button.select-button {
	background: #333;
    border: none;
}
.multiline-ellipsis-two:after, .multiline-ellipsis-two:before, .multiline-ellipsis-three:before, .multiline-ellipsis-three:after {
	visibility: hidden;
}

.orange {
	background: #3498db;
}
.dominos .item .domino .bottom {
	color: #3498db
}
.dominos .item .domino .top img, .dominos .item .domino .bottom {
	background: #444;
}

help p, .edit, .list li {
	color: white;
}

help #TOC ul {
	border-color: #3498db;
}

.my-notifications .responsive-table table thead tr {
	background-color: #0abde3;
}

.my-notifications .responsive-table table thead tr:hover {
	background-color: #48dbfb;
}

table tbody tr:hover, ul.list-view tbody tr:hover {
	background-color: #333!important;
}

table tr {
	border-color: #333;
}

.responsive-table .fixed-head {
	border-collapse: collapse;
}

button.select-button span.active, .action-item i.send-mail::before, .action-item i.add-favorite::before, .list li::before {
	color: #3498db;
}

.action-item:hover {
	color: #2980b9!important;
}

}
