css:
@-moz-document domain("filmweb.pl") {
.reviewBox--wide .reviewBox__content
{
	position: relative;
	z-index: 1;
	margin: -5.75rem 1rem 0;
	padding: 1.125rem 1rem 1rem;
	transition: box-shadow .5s cubic-bezier(.25, .46, .45, .94);
	background: #222b;
}

.awardImage
{
	background-color: white;
}

.filmCoverSection__buttons
{
	position: relative;
	top: 0.5rem;
}

.filmCoverSection__bg, .filmCoverSection__trailerLink, .filmOnBoarding.FilmOnBoarding, .comment__meta, .forumTopicSection__authorReply a, .sbtn.pull-right
{
	display: none;
	background-image: none;
}

html.coverPhoto.coverPhoto--horizontal .filmCoverSection__wrapper, html.coverPhoto .filmCoverSection__wrapper, html:not(.coverPhoto) .filmCoverSection__coverPhoto, html:not(.coverPhoto) .filmCoverSection__wrapper
{
	min-height: 0px;
	padding-top: 0px;
	margin-top: 0px;
}

