}

.filmAwardsSection [data-type="awards"].filmAwardsSection__groupContent
{
	background-color: #da22;
}

html.mouse .boxBadge__item a
{
	background-color: #333;
}

body, html, div, p, .forumSection__searchInput, span, .text, .input, .forumSearch, input, .starSlider__state, .starSlider__state label, .starSlider__state span, .fwBtn--default, div.review__shortDesc a, .mCommentArea textarea, .navList--secondary .navList__text, .filmMainCuriositiesSection__item, .forumSection__topicText, .filmPosterSection__plot, .forumTopicSection__topicText, .dynamicTextField__textarea, .dynamicTextField__value, .filmDescriptionSection__text, .filmInfo__infoText, .userReviewSection .review__text, .collapse__btn.collapse__full-btn .fwBtn__label .fwBtn__activeFilters
