}

/**/
body, html, section, footer, .filmEmptySection__wrapper, .hits__item, .hits .hits__item
{
	padding-top: 3px !important;
	padding-bottom: 5px !important;
}

.pageBox, .filmEmptySection, .hits__item
{
	margin-top: 3px !important;
	margin-bottom: 5px !important;
}

