{
	filter: invert(100%);
}
*/
.sbtn-switcher > li > button, .sbtn, .fwBtn--checkbox, .fwBtn__checkbox
{
	background-color: #222;
	color: #ccc;
}

.sbtn-switcher > li > button:hover, li.navList__item.navList__item--switching.navList__item--selected, body textarea[disabled]
{
	color: #222;
}

.sbtn-switcher > li > button.on, .fwBtn--beta:hover, .sbtn-switcher > li > button.on, .fwBtn--beta:hover, .nav-bar > li.active, .labelBox__category, body input[disabled], .form__wrapper .form__remove
{
	background-color: #fc4;
	color: #222;
}

html.mouse .personRole:hover
{
	background-color: #fc4;
}

.buttonsContainer button:focus, .buttonsContainer button:hover, .fwBtn--default:focus, .fwBtn--default:hover, .fwBtn__default:focus, .fwBtn__default:hover, .section__header--bwb .section__title .no-query, .section__header--bwb .section__title .query, .resultItem__title.resultItem__title--year
{
	color: #fc4;
}

span.fwBtn__label:hover, .personRole__role:hover, .navList--secondary .navList__item--selected .navList__button .navList__text, .filterSelect__box, .filterSelect__label:hover, .filterSelect__button:hover
{
	color: #222;
}

.fwBtn--center, .fwBtn__center
{
	margin-top: 0.5rem !important;
}
}
