css:
@-moz-document domain("darksky.net") {
body,
nav {
    background: #111;
}

body,
body.forecast .day,
.timeline_container .timeline .temps > span > span,
body.day .dayDetails .title .date,
#searchForm input {
    color: #ddd;
}

#header {
    background: #333;
}

#currentDetailsWrapper {
    background: #222;
}

#header .currentLocationButton,
#header .searchButton,
.selectric,
#searchForm input {
    background: #555;
}

.selectric .label,
.custom-dropdown--white::after {
    color: #eee;
}

.timeline_container .timeline .hours .hour span {
    color: #555;
}

#controls {
    background: rgba(0, 0, 0, 0.85)
}

[data-_extension-text-contrast="both"],
.custom-dropdown--white::before,
.custom-dropdown__select--white,
#datepicker-button,
#now-button {
    background: #444 !important;
    color: #ddd !important;
}

.custom-dropdown--white::before,
body.forecast .dayDetails.revealed {
    border-color: #777;
}

#left-arrow,
#right-arrow,
#header .currentLocationButton img,
#header .searchButton img,
.sunTimes > span > img {
    filter: invert(100%);
    opacity: 0.8;
}

body.forecast .moreDetails,
body.forecast #timeMachine .button {
    background: rgb(19, 50, 87);
}

.stripes {
    filter: brightness(70%) contrast(125%);
}

.skycon {
    opacity: 0.75;
}

body.day #slider .handle {
    background: rgba(255, 255, 255, 0.25);
}

/* can't style highcharts lines because it's done with an SVG attribute instead of through CSS, so use a light background for charts */
.highcharts-container {
    background: #ccc;
}
}
