css:
@-moz-document domain("!!_INFO_!!") {
/*
    
    Theme Name: FunFile Redesign

    Creator: Adam-J-Frost.
    
    Updated: 2020/01/21

    Created: 2020/01/20
    
    Description: This is a Redesign for FunFile.

*/
}

@-moz-document domain("www.funfile.org") {
:root {
    /* <- Logo Start -> */
    --logo: url("https://i.ibb.co/3WGZZ41/ff-logo.png");
    /* <- Logo End -> */
    /* <- Background Image Start -> */
    --background-image: url("/*[[bgimage]]*/");
    /* <- Background Image End -> */
    /* <- New Icons Start -> */
    /* <- File Type Icons Start -> */
    --0-day: url("https://i.ibb.co/VC7MR5B/0-day.png");
    --audiobooks: url("https://i.ibb.co/q1KKDfC/audiobook.png");
    --tv-show: url("https://i.ibb.co/0sj29TZ/tv-show.png");
    --movies: url("https://i.ibb.co/T2FB1hL/movie.png");
    --web-hd: url("https://i.ibb.co/9rsDvNs/web-hd.png");
    --web-sd: url("https://i.ibb.co/HHd6FFr/web-sd.png");
    --x264: url("https://i.ibb.co/pnLYfhN/x264.png");
    --x265: url("https://i.ibb.co/Tq6skLz/x265.png");
    --xvid: url("https://i.ibb.co/C8HFfnv/xvid.png");
    --dvd-r: url("https://i.ibb.co/MMrmnxj/dvd-r.png");
    --apps: url("https://i.ibb.co/102W7mG/app.png");
    --windows: url("https://i.ibb.co/rvKKpZp/windows.png");
    --mac-os: url("https://i.ibb.co/NKHcfV5/mac-os.png");
    --linux: url("https://i.ibb.co/WkBr0L8/linux.png");
    --games: url("https://i.ibb.co/KrZJSjR/game.png");
    --music: url("https://i.ibb.co/3T4QJrR/music.png");
    --tutorials: url("https://i.ibb.co/0F9NH6x/tutorials.png");
    --xxx-0-day: url("https://i.ibb.co/FbVLLq9/xxx-0-day.png");
    /* <- File Type Icons End -> */
    /* <- Forum Icons Start -> */
    --forum-unread: url("https://i.ibb.co/LCM1JF9/forum-unread.png");
    --forum-read: url("https://i.ibb.co/t3fYnNY/forum-read.png");
    --topic-unread: url("https://i.ibb.co/ZmZYrbH/topic-unread.png");
    --topic-read: url("https://i.ibb.co/J2wKp5D/topic-read.png");
    --topic-locked: url("https://i.ibb.co/KGQTVsM/topic-locked.png");
    /* <- Forum Icons End -> */
    /* <- RSS Start -> */
    --rss: url("https://i.ibb.co/sWbjQXD/button-rss.png");
    /* <- RSS End -> */
    /* <- Star Rating Start -> */
    --star-rating-1: url("https://i.ibb.co/kqXFWZJ/star-rating-1.png");
    --star-rating-2: url("https://i.ibb.co/wgnfz9h/star-rating-2.png");
    --star-rating-3: url("https://i.ibb.co/KV8y9dS/star-rating-3.png");
    --star-rating-hover: url("https://i.ibb.co/rtk8vsL/star-rating-hover.png");
    /* <- Star Rating End -> */
    /* <- IMDB / Meta Critics / Payment Icons Start -> */
    --imdb: url("https://i.ibb.co/GHCXRd5/button-imdb.png");
    --meta-critics: url("https://i.ibb.co/SKcZV40/button-meta-critic.png");
    --paypal-sub-button: url("https://i.ibb.co/sjr9zWc/button-paypal-subscribe.png");
    --paypal-donate-button: url("https://i.ibb.co/2dBT5Bf/button-paypal-donate.png");
    --bitcoin-donate-button: url("https://i.ibb.co/H2rvSkL/button-bitcoin-donate.png");
    /* <- IMDB / Meta Critics / Payment Icons End -> */
    /* <- Gender Start -> */
    --gender-na: url("https://i.ibb.co/9gGzC7B/gender-na.png");
    --gender-female: url("https://i.ibb.co/Bt8Z66p/gender-female.png");
    --gender-male: url("https://i.ibb.co/Qdz4gjY/gender-man.png");
    /* <- Gender End -> */
    /* <- Flags Start -> */
    --flag-canada: url("https://i.ibb.co/nQgGR9v/canada.png");
    --flag-usa: url("https://i.ibb.co/PDsVxyN/usa.png");
    /* <- Flags End -> */
    /* <- New Icons End -> */
    /* <- Donate Start -> */
    --donate-0: url("data:image/svg+xml;base64,");
    --donate-1: url("data:image/svg+xml;base64,");
    --donate-2: url("data:image/svg+xml;base64,");
    --donate-3: url("data:image/svg+xml;base64,");
    --donate-4: url("data:image/svg+xml;base64,");
    /* <- Donate End -> */
    /*[[mode]]*/
}

/*[[tc]]*/

h1,
h2 {
    color: var(--nav-text-color) !important;
}
a {
    color: var(--anchor-color-a) !important;
}
a:hover {
    color: var(--anchor-hover-color-a) !important;
}
a:focus,
a:active,
a:visited {
    color: var(--color-anchor-focus-active-visited-a) !important;
}
a:focus:hover,
a:active:hover,
a:visited:hover {
    color: var(--color-anchor-focus-active-visited-hover-a) !important;
}
body {
    background: var(--background-color) var(--background-image) !important;
    background-position: center top !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    color: var(--body-text-color) !important;
}
.main_wrapper {
    background: var(--wrapper-color) !important;
    padding-left: 25px !important;
    padding-bottom: 55px !important;
    border-bottom-left-radius: 8px !important;
    border-bottom-right-radius: 8px !important;
}
table.mainframe:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2),
table.mainframe:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {
    background: transparent !important;
}
/* <- Buttons Start -> */
.btn,
[type="submit"] {
    font-weight: bold;
    cursor: pointer;
    background: var(--button-background-color) !important;
    height: 40px;
    color: var(--button-text-color) !important;
    font-size: 12pt;
    border: none;
    border-radius: 4px !important;
    padding-left: 15px !important;
    padding-right: 15px !important;
    text-align: center !important;
    -webkit-transition: var(--trans-speed-on-1) !important;
    -moz-transition: var(--trans-speed-on-1) !important;
    -o-transition: var(--trans-speed-on-1) !important;
    transition: var(--trans-speed-on-1) !important;
}
.btn:hover,
[type="submit"]:hover {
    background: var(--button-background-hover-color) !important;
    color: var(--button-text-hover-color);
}

/* <- Buttons End -> */
/* <- Input / Select / Checkbox Start -> */
textarea,
input,
#descr {
    background-color: var(--input-select-background-color) !important;
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
    color: var(--input-select-text-color) !important;
    padding: 10px !important;
}
select {
    background-color: var(--input-select-background-color) !important;
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
    color: var(--input-select-text-color) !important;
    padding: 10px !important;
}
input[type="checkbox"] {
    border: 1px solid var(--border-color) !important;
    border-radius: 4px !important;
}
td.rowhead {
    border-right: 1px solid var(--border-color) !important;
}
fieldset {
    background: var(--box-background-color) !important;
    border-color: var(--border-color) !important;
}
/* <- Input / Select / Checkbox End -> */
.l_side,
.r_side {
    background: transparent !important;
}
[src="https://www.funfile.org/themes/Autumn/images/blank.gif"] {
    height: 176px !important;
}
.head_banner {
    background: transparent var(--logo) !important;
    background-repeat: no-repeat !important;
    background-size: 841px 176px !important;
    width: 841px !important;
    height: 176px !important;
}
[src="/pic/rssicon.png"],
[src="/themes/Autumn/images/rssicon.png"] {
    background: var(--rss) !important;
    background-size: 74px 74px !important;
    background-repeat: no-repeat !important;
    width: 74px !important;
    height: 74px !important;
    box-sizing: border-box !important;
    padding-left: 74px !important;
    border-radius: 4px !important;
    position: relative !important;
    top: 40px !important;
    left: 0px !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
[src="/pic/rssicon.png"]:hover,
[src="/themes/Autumn/images/rssicon.png"]:hover {
    opacity: 0.8 !important;
}
/* <- Donate Start -> */
.donate_out {
    height: 124px !important;
}
.donate {
    display: block !important;
    background: rgba(45, 45, 45, .5) var(--donate-0) !important;
    background-repeat: no-repeat !important;
    background-attachment: scroll !important;
    background-size: 115px 115px !important;
    -webkit-border-radius: 100%;
    -moz-border-radius: 100%;
    border-radius: 100%;
    width: 115px !important;
    height: 115px !important;
    position: relative !important;
    top: 170px !important;
    left: 860px !important;
    bottom: 0 !important;
    right: 0 !important;
    z-index: 98 !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
.donate:hover {
    background: rgba(0, 123, 145, .5) var(--donate-0) !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
    -webkit-box-shadow: 0px 0px 25px #00d8ff !important;
    -moz-box-shadow: 0px 0px 25px #00d8ff !important;
    box-shadow: 0px 0px 25px #00d8ff !important;
    -webkit-border-radius: 100% !important;
    -moz-border-radius: 100% !important;
    border-radius: 100% !important;
}

.idonate1 {
    display: block !important;
    background: transparent var(--donate-1) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-attachment: scroll !important;
    background-size: 115px 115px !important;
    width: 115px !important;
    height: 115px !important;
    position: relative !important;
    top: -15.5px !important;
    left: 0 !important;
    bottom: 0 !important;
    right: 0 !important;
    z-index: 99 !important;
}

.idonate2 {
    display: block !important;
    background: transparent var(--donate-2) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-attachment: scroll !important;
    background-size: 115px 115px !important;
    width: 115px !important;
    height: 115px !important;
    position: relative !important;
    top: -15.5px !important;
    left: 0 !important;
    bottom: 0 !important;
    right: 0 !important;
    z-index: 99 !important;
}

.idonate3 {
    display: block !important;
    background: transparent var(--donate-3) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-attachment: scroll !important;
    background-size: 115px 115px !important;
    width: 115px !important;
    height: 115px !important;
    position: relative !important;
    top: -15.5px !important;
    left: 0 !important;
    bottom: 0 !important;
    right: 0 !important;
    z-index: 99 !important;
}

.idonate4 {
    display: block !important;
    background: transparent var(--donate-4) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-attachment: scroll !important;
    background-size: 115px 115px !important;
    width: 115px !important;
    height: 115px !important;
    position: relative !important;
    top: -15.5px !important;
    left: 0 !important;
    bottom: 0 !important;
    right: 0 !important;
    z-index: 99 !important;
}
.donate > div:nth-child(2) > span:nth-child(4) {
    position: relative;
    top: -125px !important;
    color: var(--text-color-a) !important;
    font-size: 32pt !important;
    z-index: 97 !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
.donate > div:nth-child(2) > a:nth-child(2) {
    position: relative;
    top: -65px !important;
    color: var(--text-color-a) !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
.donate:hover > div:nth-child(2) > span:nth-child(4),
.donate:hover > div:nth-child(2) > a:nth-child(2) {
    color: #fff !important;
}
/* <- Donate End -> */
/* <- Username | Title | Rank | Invites | Credits - Start -> */
table.mainframe {
    position: relative !important;
    top: 85px !important;
}
.head_banner > div:nth-child(2) {
    background: var(--user-stats-background-color) !important;
    border-top: 1px solid var(--border-color) !important;
    border-left: 1px solid var(--border-color) !important;
    border-right: 1px solid var(--border-color) !important;
    border-top-left-radius: 8px !important;
    border-top-right-radius: 8px !important;
    width: 100% !important;
    min-width: 180px !important;
    max-width: 820px !important;
    position: relative !important;
    top: 51px !important;
    left: -14px !important;
    right: 0 !important;
    padding: 10px 10px 10px 10px !important;
    z-index: 999 !important;
    text-align: center !important;
}
.head_banner > div:nth-child(3) {
    background: var(--user-stats-background-color) !important;
    border-top: 1px solid var(--border-color) !important;
    border-left: 1px solid var(--border-color) !important;
    border-right: 1px solid var(--border-color) !important;
    color: var(--user-stats-text-color) !important;
    position: relative !important;
    top: 57px !important;
    left: 0 !important;
    right: 0 !important;
    width: 100% !important;
    min-width: 180px !important;
    max-width: 820px !important;
    padding: 10px 10px 10px 10px !important;
    z-index: 999 !important;
    float: left !important;
    text-align: center !important;
}
.head_banner > div:nth-child(3) > b:nth-child(5) > span:nth-child(1)[style="color: #339933;"],
.head_banner > div:nth-child(3) > a:nth-child(6) > font:nth-child(1)[color="green"] {
    color: #04bb04 !important;
}
.head_banner > div > p {
    background: var(--user-stats-background-color) !important;
    border-top: 1px solid var(--border-color) !important;
    border-left: 1px solid var(--border-color) !important;
    border-bottom: 1px solid var(--border-color) !important;
    border-right: 1px solid var(--border-color) !important;
    color: var(--user-stats-text-color) !important;
    position: relative !important;
    top: 57px !important;
    left: 0 !important;
    right: 0px !important;
    width: 100% !important;
    min-width: 180px !important;
    max-width: 820px !important;
    padding: 10px 10px 10px 10px !important;
    border-bottom-left-radius: 8px !important;
    border-bottom-right-radius: 8px !important;
    z-index: 999 !important;
    text-align: center !important;
}
/* <- Username | Title | Rank | Invites | Credits - End -> */
/* <- Gender Start -> */
[src="pic/na.gif"] {
    background: var(--gender-na) !important;
    background-size: 30px 30px !important;
    background-repeat: no-repeat !important;
    width: 30px !important;
    height: 30px !important;
    box-sizing: border-box !important;
    padding-left: 46px !important;
    border-radius: 4px !important;
}
[src="pic/male.gif"] {
    background: var(--gender-male) !important;
    background-size: 30px 30px !important;
    background-repeat: no-repeat !important;
    width: 30px !important;
    height: 30px !important;
    box-sizing: border-box !important;
    padding-left: 46px !important;
    border-radius: 4px !important;
}
[src="pic/female.gif"] {
    background: var(--gender-female) !important;
    background-size: 30px 30px !important;
    background-repeat: no-repeat !important;
    width: 30px !important;
    height: 30px !important;
    box-sizing: border-box !important;
    padding-left: 46px !important;
    border-radius: 4px !important;
}
/* <- Gender End -> */
/* <- Center Content Start -> */
/* <- Poster Start -> */
#poster {
    border: 0 !important;
    border-radius: 0 !important;
}
/* <- Avatars Start -> */
#avatar > a > img {
    border-radius: 15px !important;
    border: 4px solid var(--colored-border-color) !important;
    position: relative;
    left: 15px !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
#avatar > a > img:hover {
    border-radius: 0 !important;
    border: 4px solid var(--colored-border-color) !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
table.mainframe > tbody > tr > td > table > tbody > tr > td > table > tbody > tr > td > div {
    width: 130px !important;
    height: 100px !important;
}
#avatar_img,
html body.yui-skin-sam div.main_wrapper div.no_side table.mainframe tbody tr td.mf_content form table tbody tr td.row1 img.border {
    border-radius: 15px !important;
    border: 4px solid var(--colored-border-color) !important;
    position: relative;
    left: 15px !important;
    width: 90px !important;
    height: 90px !important;
    -webkit-transition: var(--trans-speed-on-2) !important;
    -moz-transition: var(--trans-speed-on-2) !important;
    -o-transition: var(--trans-speed-on-2) !important;
    transition: var(--trans-speed-on-2) !important;
}
#avatar_img:hover {
    border-radius: 0 !important;
    border: 4px solid var(--colored-border-color) !important;
    cursor: pointer;
}
/* <- Sidebar Left Start -> */
.l_side > div {
    position: relative !important;
    top: -150px !important;
    right: 10px !important;
    padding-right: 18px !important;
}
.mb_top,
.mb_middle,
.mb_bottoml,
.mb_bottomr {
    background: none !important;
}
.mf_tl,
.mf_tm,
.mf_tr,
.mf_ml,
.mf_mr,
.mf_bl,
.mf_bm,
.mf_br {
    background: none !important;
}
.l_side > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr > td,
.l_side > div:nth-child(3) > div:nth-child(2) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr > td,
.l_side > div:nth-child(5) > div:nth-child(2) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr > td {
    width: 140px !important;
    height: 30px !important;
    position: relative !important;
    padding: 8px 0px 8px 0px !important;
    text-align: left !important;
    font-weight: bold;
    font-size: 11pt !important;
}
.l_side > div > div > div > table > tbody > tr > td > a[href="https://www.funfile.org/users.php"],
.l_side > div:nth-child(5) > div:nth-child(2) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr > td > a {
    width: 140px !important;
    padding: 10px !important;
    text-align: left !important;
    font-weight: bold;
    font-size: 11pt !important;
    float: left;
}
.l_side > div > div > div > table > tbody > tr > td > a[href="https://www.funfile.org/users.php"]:hover,
.l_side > div:nth-child(5) > div:nth-child(2) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr > td > a:hover {
    width: 140px !important;
    padding: 10px !important;
    text-align: left !important;
}
.l_side > div > div > div > table > tbody > tr > td > a {
    display: block !important;
    color: var(--nav-text-color) !important;
    padding: 8px 80px 8px 10px !important;
    text-align: left !important;
    width: auto !important;
    border-radius: 15px !important;
    -webkit-transition: var(--trans-speed-on-1) !important;
    -moz-transition: var(--trans-speed-on-1) !important;
    -o-transition: var(--trans-speed-on-1) !important;
    transition: var(--trans-speed-on-1) !important;
}
.l_side > div > div > div > table > tbody > tr > td > a:hover {
    display: block !important;
    background: var(--nav-button-background-hover-color) !important;
    color: var(--nav-text-hover-color) !important;
    border-radius: 15px !important;
    padding: 8px 80px 8px 10px !important;
    text-align: left !important;
    -webkit-transition: var(--trans-speed-on-1) !important;
    -moz-transition: var(--trans-speed-on-1) !important;
    -o-transition: var(--trans-speed-on-1) !important;
    transition: var(--trans-speed-on-1) !important;
}
.l_side > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(7) > td:nth-child(1) > a:nth-child(1) > font {
    color: var(--nav-text-color) !important;
}
