css:
@-moz-document domain("angrytools.com") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #26519e #1c1c1c;
}
/*HEADER*/
/*HEADER*/
/*MAIN-PAGE*/
.at-toolthumb h4{
    color: #26519e;
}
.at-toolthumb p{
    color: #fff;
}
/*MAIN-PAGE*/
/*TOOL*/
#wrapper,#msg_header,ul.menucont li.item,.wrapper,.ani-keypreset{
    background: #1c1c1c !important;
    border: 1px solid #26519e;
    color: #fff;
}
.cs_bpt,.cs_tpt{
    filter: invert(100%);
}
ul.code li{
    background: #1c1c1c;
    border-color: #26519e;
    margin-top: 1px;
}
ul{
    color: #800080;
}
div.tab span,#g_layer_cont,div.codenametab span,#code,div.codetab span,.formpanel,.sp-replacer,.sp-picker-container{
    background: #1c1c1c;
    border-color: #26519e;
}
div.tab span.hide,div.codenametab span.hide,.tab span.disable_tab{
    background: #26519e;
    border-bottom: none;
    margin-bottom: 2px;
    color: #fff;
}
input,select,textarea,.code,.sp-input,.input-group .form-control,.btn:hover{
    background: #1c1c1c;
    color: #fff;
    border-color: #26519e;
}
div.wmsg div,.accordion-content .lbl{
    color: #1c1c1c;
}
.at-pg-box,.at-sidebar-box{
    background: #1c1c1c;
    box-shadow: none;
}
#btn_textcolor,.accordion-content .txtbx{
    background: #1c1c1c !important;
    color: #fff !important;
    border-color: #26519e !important;
}
span.at-codeattr,pre.at-code{
    background: #1c1c1c;
    color: #800080
}
.formpanel h2{
    text-shadow: none;
    color: #26519e;
}
.sp-container button,#code_table span.ani-code-title,.btn{
    background: #26519e;
    color: #fff;
    text-shadow: none;
    border: none;
}
ul.lmenucont li.selected,ul.lmenucont li:hover,.navbar-default .navbar-nav > li > a{
    background: #26519e;
    color: #fff;
}
.sp-dd{
    color: #26519e;
}
.CodeMirror,.CodeMirror-linenumber,.CodeMirror-gutter,.CodeMirror-scrollbar-filler,.navbar-default .navbar-nav > .active > a{
    background: #1c1c1c;
    color: #fff;
}
.cm-s-default .cm-qualifier{
    color: #3079ed
}
.cm-s-default .cm-meta{
    color: #a11
}
svg path{
    fill: #fff;
}
ul.lmenucont li span,p{
    color: #fff;
}
.navbar-default{
    background: #1c1c1c;
    border: none;
}
.btn-default{
    background: #1c1c1c;
    text-shadow: none;
    color: #26519e;
    border-color: #26519e;
    box-shadow: none;
}
tr{
    background: #1c1c1c;
}
/*TOOL*/
/*SUBPAGE*/
/*DONATE*/
.at-container{
    background: #1c1c1c;
}
/*DONATE*/
/*DEVELOPER*/
.at-row.at-margin-t-150.at-container{
    top: -176px;
}
.at-margin-t-150{
    background: #1c1c1c !important;
}
.at-row{
    margin-right: -17px;
}
.at-container a{
    color: #26519e !important;
}
/*DEVELOPER*/
/*CONTACT*/
.at-frm-comment form{
    background: #1c1c1c;
}
.at-frm-comment b{
    color: #fff;
}
/*CONTACT*/
/*SUBPAGE*/
/*FOOTER*/
.at-footer p{
    color: #fff;
}
.at-footer p span:after{
    content: " | Dark theme by Maciejozaman";
    color: #26519e;
}
/*FOOTER*/
}
