css:
@-moz-document domain("cloudconvert.com") {
html,body{
    background: #1c1c1c !important;
    color: #fff;
}
:root{
    scrollbar-color: #b53836 #1c1c1c;
}
/*HEADER*/
.bg-gradient-navbar,.dropdown-menu{
    background: #1c1c1c;
}
.dropdown-menu-header,.navbar-dark .navbar-nav .nav-link:hover{
    color: #b53836;
}
.navbar-nav .mega-dropdown-menu ul li a{
    color: #fff;
}
.navbar-nav .mega-dropdown-menu ul li a:hover{
    background: #b53836;
}
/*HEADER*/
/*MAIN-PAGE*/
.app-content{
    background: #1c1c1c;
}
.dropdown-item,.mb-0{
    color: #fff;
}
.dropdown-item:hover{
    background: #b53836;
}
#content .row h3{
    color: #b53836;
}
.fa-copy,.fa-shield-check,.fa-cogs,.fa-cubes{
    color: #b53836;
}
/*MAIN-PAGE*/
/*SUBPAGES*/
.card,.card-header{
    background: #1c1c1c;
}
.card .card-title,html body .content .content-wrapper .content-header-title{
    color: #b53836;
}
.card-body a:hover,.btn-secondary:hover{
    background: #b53836;
    color: #1c1c1c;
    border-radius: 3px;
}
.text-nowrap{
    color: #fff;
}
.card-content{
    border: 1px solid #b53836;
}
.btn-outline-secondary,.converter .converter-header .format-dropdown button{
    border-color: #b53836;
    color: #fff;
}
.converter .converter-header .format-dropdown button:hover{
    background: #b53836;
    color: #1c1c1c;
}
.btn-primary:hover{
    background: #1c1c1c;
}
/*API*/
.main-menu.menu-light{
    border: none;
}
.main-menu.menu-light .navigation{
    background: #1c1c1c;
}
.navigation li a,.main-menu.menu-light .navigation > li ul .active > a,.table-hover tbody tr:hover > td{
    background: #1c1c1c;
}
.main-menu.menu-light .navigation li a,.table,.nav-pills > li > a{
    color: #fff;
}
code{
    color: purple;
    background: #1c1c1c;
}
.main-menu.menu-light .navigation .navigation-header span{
    color: #b53836;
}
.nav-list > li > a, .nav-list .nav-header{
   color: #fff;
   text-shadow: none;
}
.nav.condensednav > li > a:hover{
    color: #1c1c1c;
}
.btn{
    background: #1c1c1c;
    color: #fff;
    text-shadow: none;
    border: none;
}
.btn:hover, .btn:focus{
    background: #b53836;
    color: #1c1c1c;
}
/*API*/
/*LOGIN*/
form label{
    color: #fff;
}
.has-icon-left .form-control,form .form-control{
    background: #1c1c1c;
    color: #fff;
    border-color: #b53836;
}
form .form-control:focus{
    background: #1c1c1c;
    color: #fff;
    border-color: #b53836;
}
.custom-checkbox .custom-control-label::before{
    background: #1c1c1c;
    border-color: #b53836;
}
/*LOGIN*/
/*SUBPAGES*/
/*FOOTER*/
footer .text-black-50{
    color: #fff !important;
}
footer .text-black-50:after{
    content: " | Dark theme by Maciejozaman";
    color: #b53836;
}
/*FOOTER*/
}
