css:
@-moz-document url-prefix("https://www.okanime.com/animes/"), url-prefix("https://www.okanime.com/cdn/anti-dmca/embed/"), url-prefix("https://www.okanime.com") {
#content > div > div.video-holder > div{
  width:100% !important;
}
#frame{height:99% !important;}
#topbar, 
widgetbot-crate,
#content > div > div:nth-child(5),
#ads
{display: none !important;}
.logo{zoom:.8;}
.border-rose { border-radius: 5px; overflow: hidden;}
body {font-family: "DroidArabicKufi", sans-serif;}

.last-animes-carousel, .last-updates-carousel>.container{width: 100% !important;}
.last-animes-carousel li{width: 245px !important;}
.last-animes-carousel .title b, #last-animes_toggle{color: #AAA !important;}
.last-updates-carousel,#content,footer{background: #171820 !important;}
.last-animes-carousel .new-item-box{background: #313241 !important; border-radius: 4px !important;}
.last-animes-carousel, .last-animes-carousel li{background: transparent !important;}
#rankings h2, .programs-carousel .title{color: #CCC !important;}


}
