css:
@-moz-document domain("ecosia.org") {
/* Ad results are lighter colored */
.results-ads,
.sidebar-product-ad-block,
.results-ads.result.js-sidebar-ad > .result-body {
    opacity: 0.85;
    font-weight: lighter
}

/* Ad results are in italics */
.results-ads,
.sidebar-product-ad-block,
.results-ads.result.js-sidebar-ad > .result-body {
    font-style: oblique
}

/* Makes the "Ad" disclaimer box stand out more */
.js-ad-hint {
    background-color: #dcdbf9!important;
    color: #000!important;
    font-weight: bold!important;
    font-style: normal;
    opacity: 1
}

/* Makes the headings of ads a different color */
.result-title-ad.result-title,
.result-title-sidebar-ad,
.sidebar-product-ad-title,
.card-ad .js-result-title {
    opacity: 1;
    color: #423c96
}

/* Makes the headings of ads less bold */
.result-title-ad.result-title,
.result-title-sidebar-ad,
.sidebar-product-ad-title,
.card-ad .js-result-title {
    font-weight: normal
}

/* Makes it so advertised prices in shopping results are less bold */
.sidebar-product-ad-price,
.sidebar-product-ad-shipping {
    font-weight: normal;
    color: #5b5b5b;
    opacity: 1
}

/* Makes merchant URL/name a lighter color */
.sidebar-product-ad-seller-name,
.card-ad .result-url,
.results-ads.result.js-sidebar-ad > .result-body > .result-url-wrapper > .result-url {
    color: #3f705d
}

/* Underlined ad heading */
.result-title-ad.result-title,
.result-title-sidebar-ad,
.sidebar-product-ad-title,
.card-ad .js-result-title {
    text-decoration-line: underline;
    text-decoration-style: solid;
    text-decoration-color: #cbc9f2
}

/* Underlined merchant URL/name */
.sidebar-product-ad-seller-name,
.card-ad .result-url,
.results-ads.result.js-sidebar-ad > .result-body > .result-url-wrapper > .result-url {
    text-decoration-line: underline;
    text-decoration-style: dotted;
}

/* Makes it so advertised prices in shopping results are kept de-italicized */
.sidebar-product-ad-price,
.sidebar-product-ad-shipping {
    font-style: normal
}
}
