body {
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: Arial,sans-serif;
    font-size: 14px;
    line-height: 18px;
    color: #000000;
    background: #1a1a1a;
    overflow-y: scroll;
    visibility: visible!important;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
}

body, h1, h2, h3 {
    font-weight: 400;
}

.header, .nav {
    background-color: #1a1a1a;
    position: relative;
}

.forecast_frame.forecast_now .forecast_wrap {
    background-color: #313131;
    padding: 10px;
    border-radius: 0 5px 5px;
}

\\\first tab
.tabs._center .tab:nth-child(2), .tabs._left .tab:nth-child(1), .tabs._right .tab:nth-child(3) {
    height: 115px;
    margin-bottom: 0;
    padding-bottom: 20px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    background-color: #313131;
    box-shadow: none;
    z-index: 10;
}

.tabs .tab {
    display: inline-block;
    width: 213.33px;
    height: 105px;
    margin: 10px 0 10px 10px;
    padding: 6px 0 6px 10px;
    vertical-align: top;
    transform: translate3d(0,0,0);
    z-index: 10;
    border-radius: 5px;
    background-color: #313131;
    box-shadow: 0 0 20px 0 rgba(150,150,150,.1);
}

.tabs._center .tab:nth-child(2) .tab_wrap:before, .tabs._center .tab:nth-child(2):before, .tabs._left .tab:nth-child(1):before, .tabs._right .tab:nth-child(3):before {
    content: '';
    bottom: 0;
    width: 5px;
    height: 5px;
    background-color: #313131;
}

.tabs._center .tab:nth-child(2) .tab_wrap:after, .tabs._center .tab:nth-child(2):after, .tabs._left .tab:nth-child(1):after, .tabs._right .tab:nth-child(3):after {
    content: '';
    bottom: 0;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background-color: #313131;
}

.gray {
    color: #159ffe;
}

.meteostations {
    border-radius: 5px;
    background-color: #313131;
}

.meteostation {
    display: table;
    width: 100%;
    padding: 10px;
    white-space: nowrap;
    color: #159ffe;
    position: relative;
    height: 56px;
}

body {
    margin: 0;
    padding: 0;
    min-height: 100vh;
    font-family: Arial,sans-serif;
    font-size: 14px;
    line-height: 18px;
    color: #000;
    background: #1a1a1a;
    overflow-y: scroll;
    visibility: visible!important;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
}

.widget__title {
    text-align: center;
    padding: 0 0 10px;
    background-color: #313131;
    position: relative;
    z-index: 1;
    box-sizing: border-box;
    margin: 0;
}

.widget__footer {
    font-size: 13px;
    line-height: 15px;
    border-radius: 0 0 5px 5px;
    padding: 10px 15px;
    position: relative;
    z-index: 1;
    background-color: #313131;
    overflow: hidden;
}

.maps_frame {
    padding: 15px;
    background-color: #313131;
    height: 430px;
}

.tabs._center .tab:nth-child(2), .tabs._left .tab:nth-child(1), .tabs._right .tab:nth-child(3) {
    height: 115px;
    margin-bottom: 0;
    padding-bottom: 20px;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    background-color: #313131;
    box-shadow: none;
    z-index: 10;
}

.whiteBlockWithShadow, .widget {
    border-radius: 5px;
    background-color: #313131;
}

.widget__type-title {
    background-color: #313131;
    padding: 5px 15px;
    position: relative;
    z-index: 2;
}

.semiwhite, .white {
    color: #000;
    border-bottom-color: rgba(255,255,255,.4);
}

.uvb-modal-open {
    margin-top: 6px;
    padding: 0;
    font-size: 13px;
    line-height: 15px;
    font-family: Arial,sans-serif;
    color: #009FFF;
    background: #313131;
    border: none;
    cursor: pointer;
}

.gm {
    position: relative;
    border-radius: 5px;
    background-color: #313131;
    box-shadow: 0 0 20px 0 rgba(150,150,150,.1);
    padding: 15px 15px 10px;
}

.forecast_frame .headerline .title {
    position: relative;
    display: inline-block;
    background-color: #313131;
    font-size: 14px;
    padding: 2px 5px 2px 12px;
    z-index: 5;
}

.cities.cities_frame {
    border-radius: 5px;
    background-color: #313131;
}

.weather_now .information .info_item .ii {
    position: relative;
    display: inline-block;
    width: initial;
    background-color: #313131;
}

.cities .cities_clear, .cities .cities_title {
    position: relative;
    background-color: #313131;
    z-index: 1;
}

.weather_current {
    position: relative;
    width: 660px;
    background-color: #313131;
    box-shadow: 0 0 20px 0 rgba(150,150,150,.1);
}

.weather_current .weather_frames {
    position: relative;
    width: 630px;
    min-height: 300px;
    border-radius: 5px;
    background-color: #313131;
    box-shadow: 0 0 20px 0 rgba(150,150,150,.1);
    box-sizing: border-box;
}

.catalog_block {
    padding: 10px;
    margin-bottom: 10px;
    border-radius: 5px;
    background-color: #313131;
    box-shadow: 0 0 20px 0 rgba(150,150,150,.1);
}

.forecast_frame {
    position: relative;
    background-color: #313131;
}
