css:
@-moz-document domain("gloswojewodzki.pl") {
html,body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #fe0710 #1c1c1c;
}
/*HEADER*/
.header-wrapper,.navbar-default{
    background: #1c1c1c;
    box-shadow: none;
}
#Header1_headerimg{
    background: #fff;
    border-radius: 4px;
}
.navbar-default .navbar-nav > li > a,.navbar-form button{
    color: #fe0710;
}
.navbar-default .navbar-nav > li > a:hover{
    background: #fe0710;
}
.navbar-default .navbar-nav > li:nth-child(1) > a{
    border-left-color: #fe0710;
}
.navbar-default .navbar-nav > li > a{
    border-right-color: #fe0710;
}
input[type="text"]{
    background: #1c1c1c;
    color: #fff !important;
    border-color: #fe0710 !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
blockquote,.nav-tabs > li a{
    background: #1c1c1c;
}
blockquote::before,.post-info span,.PopularPosts .meta,.nav-tabs > li.active > a, .nav-tabs > li.active > a,.post-grid .post-info .meta,.pagenav span.pages{
    color: #fff;
}
.post-content h3.title-color{
    color: #fe0710 !important;
}
.post-content h3.title-color:hover{
    color: #fff !important;
}
.PopularPosts .item-title h5,.post-item .post-content h3,.pagenav a{
    color: #fe0710;
}
#HTML13.widget h2{
    color: #4267B2
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.post-heading h1{
    color: #fe0710;
    border-color: #fe0710;
}
.post-heading{
    border: none;
}
.post-body h3 span,.post-body,.fbt-related-posts .post-content .post-info span{
    color: #fff;
}
.fbt-related-posts .title-wrapper h2 span{
    color: #fe0710;
}
.item #blog-pager-newer-link, .item #blog-pager-older-link{
    background: #fe0710;
}
a.blog-pager-older-link:hover, a.blog-pager-newer-link:hover{
    color: #fff;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*LOADER*/
.loader-mask{
    background: #1c1c1c;
}
/*LOADER*/
/*PODSTRONY*/
/*FOOTER*/
.copyrights p{
    color: #fff;
}
.copyrights p:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #fe0710;
}
/*FOOTER*/
}
