css:
@-moz-document domain("userstyles.org") {
/*V1.2.1_200717*/
/*[[th]]*/
:root {
--minr:/*[[min]]*/;
--scnr:/*[[scn]]*/;
--acnr:/*[[acn]]*/;
--txnr:/*[[txn]]*/;
}
/*min*/
#header, .header-search .search_input .input_text_search, .navigation .links-box .navigation-link:hover, #nav-view-responsive, #footer, #notes_textarea, body, select, #Head, .us-stylecard__button-container .us-button, #header-mobile, .mobile-search-form, #footer-mobile, #style_installed_button, .Preview {
    background: var(--minr);
}
/*scn*/
.header-search .type_search, .header-search .type_search .type_search_select, #create_new_style, .navigation, .oval, .see_more, .us-stylecard--short, #top_android_button, #top_android_button:hover, .choose_editor .boxesContainer .box, .css_button, #stylish-code, #send_feedback_button, .div-arrow, .white_button, .customize_button, .advancedsettings_shown, .MeButton:hover, .Button, .MenuItems, .Item.Read, .btn-auth, .personal-page .light-blue-button, .us-stylecard--long, pre, .mobile-search-input, div.Popup .Body  {
	background: var(--scnr);
}
/*scnimp*/
input, textarea {
    background: var(--scnr)!important;
}
/*txn*/
.header-search .type_search .type_search_text, .header-search .type_search .type_search_select .type_search_option, .header-search .type_search .type_search_select .type_search_option:hover, .header-search .search_input.focused .input_text_search, #create_new_style, .navigation .links-box .navigation-link .navigation-title, .navigation .navigation-category, #style-info-author-carousel a, .category_title, .see_more, .us-stylecard--short .us-updated, h4 a:hover, .us-stylecard--short .us-username a, .us-stylecard--short .us-username, .us-stylecard--short .us-downloads__count, .us-stylecard--short .us-discussions__count, #top_android_button .android_button_text, #copyrights, h1, .choose_editor .boxesContainer .box .title, .choose_editor .boxesContainer .box .subtitle, .choose_editor .boxesContainer .box .cta, .by-author, #install_style_button, #headline, #information_key_left, #infomation_value_left, #information_key, #information_value, #applies_link, .text_style_page, .css_button, #notes_textarea, #stylish-code, #send_feedback_button, #discussion_date, .fzKkUK, .current, #pages a, #pages a:active, #pages a:visited, .pageNumber, h4, h4 a:visited, h4 a, .ckfDBv, .white_button, .no-select.customize_button_text, body, :not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.us-username):not(.NotificationLine-Content) > a, .title_setting, input, textarea, select, #archive_style_button:hover, #archive_style_button, #Head, .Tag, .ItemDiscussion .Meta, .DataList .Meta, .DiscussionForm label, .AlertMessage, .DismissMessage, .personal-page .light-blue-button, .us-stylecard--long .us-updated, .us-stylecard--long .us-username, .us-stylecard--long .us-downloads__count, .us-stylecard--long .us-discussions__count, .us-stylecard__button-container .us-button, #update_style_button, #style-info-author-carousel a:visited, .mobile-search-type, #style_installed_button, ::selection, .Preview {
    color: var(--txnr)!important;
}
/*acnbcg*/
.header-search .type_search .type_search_select .type_search_option:hover, .oval-active, .see_more:hover, .choose_editor .boxesContainer .box .cta, #install_style_button, #install_style_button:hover, .css_button:hover, #send_feedback_button:hover, .fzKkUK, .current, .ckfDBv, .white_button:hover, .customize_button:hover, .Tag, .AlertMessage, .InfoMessage, .btn-openid:hover, .btn-openid:focus, .btn-password:hover, .btn-password:focus, .personal-page .light-blue-button:hover, .us-stylecard__button-container .us-button:hover, #update_style_button, ::selection  {
    background: var(--acnr);
}
/*acnbrd*/
#create_new_style:hover, #top_android_button:hover, .statistic table, th, td, #archive_style_button:hover, #archive_style_button, .mobile-search-type-active {
    border-color: var(--acnr);
}
/*acntxn*/
#style-info-title-carousel, #style-info-author-carousel a:hover, .bottomLinks, #as_userscript a, #as_userscript a:hover, #as_userscript a:visited, #as_userscript a:visited:hover, #discussion_author a, #discussion_author a:visited, #discussion_author a:visited:hover, .us-stylecard--long .us-username a, .mobile-search-type-active {
    color: var(--acnr)!important;
}
/*rbrd*/
#create_new_style, .navigation .links-box .navigation-link, .navigation .links-box, .choose_editor .boxesContainer .box .cta, #install_style_button, #install_style_button:hover, #send_feedback_button, #single_discussion, #title_div, .fzKkUK, .div-arrow, .white_button, .customize_button, .Button, .MenuItems, .AlertMessage, .InfoMessage, .btn-auth, #update_style_button, pre, #style_installed_button {
    border: transparent;
    box-shadow: none;
}
/*rbrdimp*/
input, textarea, select {
    border: transparent!important;
padding: 4px;
}
/*rbxs*/
#archive_style_button, .us-stylecard__button-container .us-button:hover, .us-stylecard--short .us-stencil {
    box-shadow: none;
}
/*brdbr*/
.header-search .type_search, #create_new_style, .us-stylecard--short, #top_android_button, .choose_editor .boxesContainer .box .cta, .choose_editor .boxesContainer .box, #install_style_button, #stylish-code, #send_feedback_button, .see_more, .fzKkUK, .current, .div-arrow, .ckfDBv, .white_button, .customize_button, .advancedsettings_shown, #archive_style_button, .personal-page .light-blue-button, .us-stylecard--long, .us-stylecard__button-container .us-button, #update_style_button, .mobile-search-input, #style_installed_button  {
    border-radius: 0;
}
.header-search .type_search .type_search_button {
    border-top-color: var(--acnr);
}
.navigation .links-box .navigation-link-active {
    border-left: 2.5px solid 
    var(--acnr);
}
#overlay-info {
    background: rgba(0,0,0,0.5);
}
#search_input, #create_new_style, #css_button, .white_button, .no-select, .style_card, .navigation_link, .see_more, .style_category_card_wrapper, .us-stylecard--short {
    transition: all 0.3s ease;
}
.choose_editor {
    background: rgba(0,0,0,0.7)
}
.Sprite16, .OptionsTitle, .eye_image {
    filter: invert(0.5)
}
.edit-setting {
    background: #808080;
}

input {
    margin-right: 8px;
}
.Border {
    background: transparent;
}
}
