@-moz-document domain("typeracer.com") {

* {
    font-family: consolas;
    background-color: #1f252f !important;
    color: #e4f0ff;
}

div {
    font-size: 101%;
}

iframe {
    display: none;
}

a {
    color: #ff006f;
    text-decoration: none;
}

a:hover {
    color: #ff006f;
    animation: a-hover-1 1.5s infinite;
}

#dUI > table > tbody > tr:nth-child(2) > td:nth-child(2) > div > div.mainViewport {
    width: 100%;
}

.progressBar {
    background-color: transparent;
    border-bottom-style: double !important;
    border-color: #4b5f82 !important;
}

#dUI > table > tbody > tr > td:nth-child(1) {
    display: none;
}

#dUI > table > tbody > tr:nth-child(2) > td:nth-child(2) {
    margin: 0 auto !important;
    width: 100% !important;
}

.mainViewportHolder {
    margin: 0 35%;
    width: 40%;
}

table > tbody > tr:nth-child(2) {
    line-height: 2em;
    letter-spacing: 2px !important;
}

#gwt-uid-15 > table > tbody > tr:nth-child(4) > td > div > table > tbody > tr:nth-child(4) > td > table > tbody > tr:nth-child(9) > td > table > tbody > tr > td:nth-child(2) > div > div > div:nth-child(1) {
    filter: hue-rotate(110deg);
    border-radius: 15px;
}

.txtInput {
    border: 2px solid white;
    border-radius: 15px;
    padding: 0.1em 0.5em;
}

@keyframes a-hover-1 {
    50% {
        color: #c10054;
    }
    
    100% {
        color: #ff006f;
    }
}

.chrome_tl, .chrome_t, .chrome_tr, .chrome_l, .chrome_b, .chrome_bl, .chrome_br, .chrome_r {
    display: none;
}

.statsView {
    border: 2px solid #fff !important;
    box-shadow: 0px 0px 50px #000000d6;
    overflow: visible !important;
    z-index: 50;
}

table.view > tbody > tr > td:nth-child(3) {
    opacity: 0;
}

.gwt-TabBarItem-wrapper {
    text-decoration: none;
    border-bottom: 0px;
}

.gwt-TabBarItem {
    text-decoration: none !important;
    color: white !important;
}

.footer {
    background-color: #1f252f;
    background: none;
}

.mainMenu > table {
    text-align: center;
    width: 100%;
}

#dUI > table > tbody > tr > td > div > div.mainViewport > div > table > tbody > tr > td > table {
    width: 100%;
    text-align: center;
}

#dUI > table > tbody > tr:nth-child(2) > td:nth-child(2) > div > div.mainViewport > div > table > tbody > tr> td > table > tbody > tr > td > img {
    display: none;
}
}
