css:
@-moz-document domain("myanimelist.net")
{
  /* ----------------- STATUS COMOB LIST THINGIES ------------------*/
   .anime-detail-header-stats .user-status-block {
      visibility: hidden;
  }
  /* ---------------- SCORE SHIT -------------------*/
   .anime-detail-header-stats .stats-block .score {
      visibility: hidden;
      width: 0;
  }
  .anime-detail-header-stats .stats-block .numbers {
      visibility: hidden;
  }
  .anime-detail-header-stats .stats-block .information {
      font-size: 25px !important;
  }
  .anime-detail-header-stats .stats-block:before {
      visibility: hidden;
  }
  .anime-detail-header-stats .stats-block .information-block {
      position: absolute;
      top: 23px;
      left: 10px;
  }
  .page-common .pb16 {
      padding-bottom: 0 !important;
  }
  /* -------- STATISTICS ----------- */
   .page-common .po-r {
      visibility: hidden;
  }
  .page-common .po-r.clearfix {
      visibility: visible;
  }

  /* -------- MISC STUFF & CLEANING ----------- */
  /* Anime stats         */ h2:nth-child(27),.js-scrollfix-bottom > div:nth-child(30),.spaceit:nth-child(31),.js-scrollfix-bottom > div:nth-child(32),
  /* Anime news          */ h2:nth-child(31), .clearfix:nth-child(32), .clearfix:nth-child(34), .borderClass:nth-child(33), .borderClass:nth-child(35),
  /* Hide anime articles */ h2:nth-child(40), .news-list,
  /* Hide reccomended    */ .btn-detail-recommendations-view-all,
  /* Hide anime header   */ #horiznav_nav,
  /* Hide bread-crumb    */ .breadcrumb,
  /* Hide social media   */ .js-sns-icon-container,
  /* Hide external links */ h2:nth-child(34), .pb16:nth-child(35),
  /* Hide broken spacing */ div:nth-child(26), .amazon-ads
  {
      visibility: hidden !important;
      height: 0; 
      border: 0;
      margin: 0;
      padding: 0;
  }

  /* -------- REVIEWS ----------- */
  h2:nth-child(19), .borderDark:nth-child(20), .borderDark:nth-child(21), .borderDark:nth-child(22), .borderDark:nth-child(23) {
      visibility: hidden !important;
      height: 0; 
      border: 0;
      margin: 0;
      padding: 0;
  }

  /* -------- RECCOMENDATIONS ----------- */
  h2:nth-child(28), .pb24 > div:nth-child(29){
      visibility: hidden !important;
      height: 0;
      border: 0;
      margin: 0;
      padding: 0;
  }

  /* -------- FORUM DISCUSSION ----------- */
  h2:nth-child(37), .page-forum
  {
      visibility: hidden !important;
      height: 0; 
      border: 0;
      margin: 0;
      padding: 0;
  }
}
