css:
@-moz-document url-prefix("https://www.poradnikzdrowie.pl/") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #3597db #1c1c1c;
}
/*HEADER*/
header .top_menu,header .bottom_menu,.bottom_menu::before{
    background: #1c1c1c;
}
header{
    box-shadow: none;
    border-bottom: 1px solid #fff;
}
.menu-box li a{
    color: #3597db;
}
.menu-box li:hover a{
    color: #fff;
}
header .submenu-box .gl_wrapper{
    border: none;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.listing-container .element .element__headline a{
    color: #3597db !important;
}
.listing-container .element:hover .element__headline a{
    color: #fff !important;
}
.element,.element__content,.element__headline{
    background: #1c1c1c !important;
}
.main-title-listing{
    color: #3597db;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.article h1,.article h2,.rating__question .rating__questionText,.rating__form .rating__formText{
    color: #3597db;
}
.recommended-content{
    background: #1c1c1c;
    border: 1px solid #3597db;
}
.expert-block,.tags .breadcrumb__link,.rating__form .rating__form__cancel{
    background: #1c1c1c;
}
.tags .breadcrumb__link:hover,.rating__message,.breadcrumb__link:hover{
    background: #3597db;
    color: #1c1c1c;
}
.rating__scale #btn_like::after{
    color: green;
}
.rating__scale #btn_unlike::after{
    color: red;
}
.rating .rating__value,.rating__form label,.highlight__textbox p{
    color: #fff;
}
.rating__form textarea{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #3597db;
}
.breadcrumb__link{
    background: #1c1c1c;
    color: #3597db;
    border: 2px solid #3597db;
}
/*QUIZ*/
.quiz:not(.item){
    box-shadow: none;
    border-left-color: #3597db
}
#ajax_quiz{
    background: #1c1c1c;
}
.quiz h2{
    background: #3597db;
    color: #1c1c1c;
}
.quiz p{
    color: #fff;
}
.quiz span{
    color: #3597db;
}
.quiz .answers .item{
    border: 2px solid #3597db;
}
.quiz .quiz_send_form:hover,.quiz .answers .item .selected{
    background: #3597db;
}
.quiz a#quiz_next_question, .quiz a#quiz_show_result,.quiz .buttons a{
    background: #1c1c1c;
    color: #3597db;
    border: 2px solid #3597db;
}
.quiz a#quiz_next_question:hover, .quiz a#quiz_show_result:hover,.quiz .buttons a:hover{
    background: #3597db;
    color: #1c1c1c;
}
/*QUIZ*/
.other-embed iframe{
    max-width: 91%;
}
/*KOMENTARZE*/
.comments__sectionTopText--author .comments__sectionTopText--authorText,.comments__sectionTopText--time .comments__sectionTopText--timeBox{
    color: #3597db;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
.blockinfo__text h1, .blockinfo__text h2{
    color: #3597db;
}
.listingItem__textLead,.blockinfo__text p{
    color: #fff;
}
.paginacja li.disable:first-child{
    background: #1c1c1c;
    color: #3597db;
    border: 1px solid #3597db;
}
/*PODSTRONY*/
/*FOOTER*/
footer .copyright .copyright__text:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #3597db;
}
/*FOOTER*/
}

@-moz-document url-prefix("https://jeszcolubisz.poradnikzdrowie.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #52b500 #1c1c1c;
}
/*HEADER*/
#topbar{
    background: #1c1c1c;
}
.topbar__item, .topbar__item a,.topbar__login{
    color: #52b500;
}
.topbar__item:hover, .topbar__item a:hover{
    color: #fff;
}
.topbar__logo{
    background: #fff;
    border-radius: 15px;
    border: 2px solid #52b500;
}
.topbar__login:hover{
    background: #52b500;
    color: #1c1c1c;
}
.topbar__login:before{
    filter: invert(100%);
}
.topbar__login:hover.topbar__login:before{
    filter: invert(0%);
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.intro__app{
    background: #1c1c1c;
}
#intro .big_foto h2,.intro__item,.interactive_images .box a .txt p{
    color: #52b500;
}
#intro .big_foto h3,.text-container h2{
    color: #fff;
}
.intro__description{
    color: #1c1c1c;
}
.interactive_images .box a{
    border: 1px solid #1c1c1c;
}
.interactive_images .box a:hover{
    border: 1px solid #52b500;
}
/*STRONA-GŁÓWNA*/
/*PODSTRONY*/
/*DIETA*/
#quote,.container--quote,#flip-box{
    background: #1c1c1c;
}
.tab-swiper__tab.active{
    color: #52b500;
}
.tab-swiper__tab{
    color: #fff;
}
/*DIETA*/
/*JAK-ZACZAC*/
#krok2, #krok4, #krok6{
    background: #1c1c1c;
}
/*JAK-ZACZAC*/
/*PODSTRONY*/
/*FOOTER*/
/*FOOTER*/
}

@-moz-document url-prefix("https://dieta.poradnikzdrowie.pl") {
body{
    background-color: #1c1c1c;
    color: #fff;
}
}
